// export default ScrollToTop;
import React, { useState, useEffect } from 'react';
import './ScrollToTopButton.css'; // Styling remains the same

const ScrollToTopButton = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [isAtBottom, setIsAtBottom] = useState(false);

  // Show button when page is scrolled down
  const toggleVisibility = () => {
    if (window.pageYOffset > 0) {
      setIsVisible(true);
    } else {
      setIsVisible(false);
    }

    // Check if the user has scrolled near the bottom
    if (
      window.innerHeight + window.pageYOffset >=
      document.body.offsetHeight - 20
    ) {
      setIsAtBottom(true);
    } else {
      setIsAtBottom(false);
    }
  };

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  };

  useEffect(() => {
    window.addEventListener('scroll', toggleVisibility);
    return () => {
      window.removeEventListener('scroll', toggleVisibility);
    };
  }, []);

  return (
    <div className={`scroll-to-top ${isAtBottom ? 'at-bottom' : ''}`}>
      {isVisible && (
        <dc-ui-button variant="secondary" size="large" type="button" hide-label="false" disabled="false" onClick={scrollToTop} className="scroll-to-top-button"><dc-ui-icon name="dc_up_double" color="black" size="large" ></dc-ui-icon></dc-ui-button>
      )}
    </div>
  );
};

export default ScrollToTopButton;
