import React, { useState, useEffect } from "react";
import { defineCustomElements } from '@boschrexroth/nextgen-web-ui-toolkit-react';
defineCustomElements();

const Preview2DApi = () => {
    const [data, setData] = useState(null);
    const [error, setError] = useState(null);
    const [selectedImage, setSelectedImage] = useState("");
    const [imageLoading, setImageLoading] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const params = {
                    apikey: 'e66120f9d9624823884cac1bf290ea88',
                    path: 'bosch_rexroth_cytro/power_units/std_power_units/cytropac_asmtab.prj'
                };
                const queryString = new URLSearchParams(params).toString();
                const url = `https://webapi.partcommunity.com/service/derivationviews?${queryString}`;
                const response = await fetch(url);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const result = await response.json();
                setData(result);
                if (result.views.length > 0) {
                    setSelectedImage(result.views[0].imageUrl);
                    setImageLoading(true);
                }
            } catch (error) {
                console.error('Error fetching the data', error);
                setError(error.message);
            }
        };

        fetchData();
    }, []);

    const handleSelectionChange = (event) => {
        setSelectedImage(event.target.value);
        setImageLoading(true);
    };

    const handleImageLoad = () => {
        setImageLoading(false);
    };

    if (error) {
        return <div>Error: {error}</div>;
    }

    if (!data) {
        return <div>Loading...</div>;
    }

    return (
        // <div>
        //     <div style={{width: "375px",}}>
        //     <dc-ui-dropdown-wrapper label="Views:" label-position="default">
        //     <select onChange={handleSelectionChange} value={selectedImage}>
        //         {data.views.map((item, index) => (
        //             <option key={index} value={item.imageUrl}>
        //                 {item.desc}
        //             </option>
        //         ))}
        //     </select>
        //     </dc-ui-dropdown-wrapper>
        //     </div>
        //     <br></br>
        //     {selectedImage && (
        //         <div style={{width: '350px', height: '350px' ,border: "2px solid rgba(0, 0, 0, 0.6)", padding: "10px"}} >
        //             {imageLoading && <div><center style={{paddingTop: '100px'}}><dc-ui-progress-icon size="large" message="Loading image..."></dc-ui-progress-icon></center></div>}
        //             <img
        //                 src={`https://webapi.partcommunity.com${selectedImage}`}
        //                 onLoad={handleImageLoad}
        //                 style={{ width: '340px', height: '340px', display: imageLoading ? 'none' : 'block'}}
        //                 alt="Selected view"
        //             />
        //         </div>
        //     )}
        // </div>
        <div>
  <div style={{ width: '90%' }}>
    <dc-ui-dropdown-wrapper label="Views:" label-position="default">
      <select onChange={handleSelectionChange} value={selectedImage}>
        {data.views.map((item, index) => (
          <option key={index} value={item.imageUrl}>
            {item.desc}
          </option>
        ))}
      </select>
    </dc-ui-dropdown-wrapper>
  </div>
  <br></br>
  {selectedImage && (
    <div style={{ width: '90%', height: 'auto', border: '2px solid rgba(0, 0, 0, 0.6)', padding: '2%' }}>
      {imageLoading && (
        <div>
          <center style={{ paddingTop: '25%' }}>
            <dc-ui-progress-icon size="large" message="Loading image..."></dc-ui-progress-icon>
          </center>
        </div>
      )}
      <img
        src={`https://webapi.partcommunity.com${selectedImage}`}
        onLoad={handleImageLoad}
        style={{ width: '100%', height: 'auto', display: imageLoading ? 'none' : 'block' }}
        alt="Selected view"
      />
    </div>
  )}
</div>

    );
};

export default Preview2DApi;
