@include dialogAnimation(dialog, true);

@include media-min('xl') {
  @include dialogAnimation(dialog, false);
}

dialog {
  position: fixed;
  top: var(--header-height);
  margin-top: 0;
  width: 100%;
  max-width: var(--header-max-width);
  box-sizing: border-box;
  border: none;
  background-color: $color-grey-01;
  box-shadow: 0px 20px 20px 0px rgba(80, 110, 129, 0.25);
  padding: 0;
  height: fit-content;
  overflow: visible;

  @include media-max('xl') {
    height: auto;

    &[open] {
      display: flex;
      flex-direction: column;
    }
  }

  &::backdrop {
    opacity: 0;
  }
}

.dialog-body {
  flex: 1;
  padding-block: 24px 64px;
}

.dialog-content {
  box-sizing: border-box;
  max-width: 1200px;
  padding: 0 15px;
  margin-inline: auto;
  position: relative;
  display: grid;
  grid-template-columns: 1fr;
  grid-auto-flow: row;
  grid-template-rows: [btn-back-start] auto [btn-back-end] 32px [title-start] auto [title-end] 32px [content-start] auto [content-end];

  @include media-min('xl') {
    grid-template-rows: [btn-close-start] auto [btn-close-end] 16px [title-start] auto [title-end] 32px [content-start] auto [content-end];
  }
}

#close-icon {
  grid-area: btn-close;
  grid-column: 1 / -1;
  margin-left: auto;
}

.title-wrapper {
  grid-area: title;
  grid-column: 1 / -1;
}

.content-wrapper {
  grid-area: content;
  grid-column: 1 / -1;
  display: grid;
  grid-auto-flow: dense;
  grid-template-columns: 1fr;
  gap: 24px;

  @include media-min('xl') {
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  }
}

.back-button {
  grid-area: btn-back;
  grid-column: 1 / -1;
  display: flex;
  align-items: center;
  gap: 8px;
  appearance: none;
  border: none;
  background: none;
  cursor: pointer;
  @include font-style('mid-size-text');
  color: $color-dark-blue-02;
  width: fit-content;
}
import { Component, h, Element, Method, Prop, State } from '@stencil/core';
import { isBreakpointEqualOrSmaller } from '../../../utils';

@Component({
  tag: 'dc-ui-header-language',
  styleUrl: 'dc-ui-header-language.scss',
  shadow: true,
})
export class DcUiHeaderLanguage {
  @Element() el: HTMLElement;
  @Prop() headline?: string;
  @Prop() noBackdropClick?: false;
  @Prop() backButtonLabel?: string;

  private dialog: HTMLDialogElement;
  @State() isMobile: boolean = false;

  private readonly updateIsMobile = () => {
    this.isMobile = isBreakpointEqualOrSmaller('xl');
  };

  // method to open the modal
  @Method() open(): Promise<boolean> {
    return new Promise((resolve) => {
      document.body.style.overflow = 'hidden';
      this.dialog.showModal();
      resolve(true);
    });
  }

  // method to close the modal
  @Method() close(): Promise<boolean> {
    return new Promise((resolve) => {
      document.body.style.overflow = '';
      this.dialog.close();
      resolve(true);
    });
  }

  @Method() toggle(): Promise<boolean> {
    return new Promise((resolve) => {
      if (this.dialog.open) {
        this.close();
      } else {
        this.open();
      }
      resolve(true);
    });
  }

  private readonly onBackdropClick = (e: MouseEvent) => {
    if (!this.noBackdropClick && e.target === this.dialog) {
      this.close();
    }
  };

  componentDidLoad() {
    this.dialog.addEventListener('click', this.onBackdropClick);
    this.updateIsMobile();
    window.addEventListener('resize', this.updateIsMobile);
  }

  disconnectedCallback() {
    this.dialog.removeEventListener('click', this.onBackdropClick);
    window.removeEventListener('resize', this.updateIsMobile);
  }

  private readonly iconClickHandler = () => {
    this.close();
  };

  render() {
    const backButton = (
      <button
        autofocus
        class="back-button"
        onClick={this.iconClickHandler}>
        <dc-ui-icon
          size="tiny"
          name="dc_backward_left"></dc-ui-icon>
        {this.backButtonLabel}
      </button>
    );

    const closeButton = (
      <dc-ui-button
        autofocus
        variant="link"
        icon="dc_close"
        hide-label="true"
        id="close-icon"
        size="small"
        onClick={this.iconClickHandler}>
        Close Modal
      </dc-ui-button>
    );

    return (
      <dialog
        class="language-dialog"
        ref={(el) => (this.dialog = el)}>
        <div class="dialog-body">
          <div class="dialog-content">
            {this.isMobile ? (this.backButtonLabel ? backButton : closeButton) : closeButton}

            <div class="title-wrapper">
              {this.headline && (
                <dc-ui-text
                  variant="h3"
                  color="darkBlue02">
                  {this.headline}
                </dc-ui-text>
              )}
              <slot name="title" />
            </div>

            <div class="content-wrapper">
              <slot name="content" />
            </div>
          </div>
        </div>
      </dialog>
    );
  }
}
