import { getCss, color as colorUtils, FULL_TEXT_STYLES } from '../../utils';
import { TCalloutVariant } from './callout-utils';

export const createStyles = (
  variant: TCalloutVariant,
  compact: boolean,
  noIcon: boolean
): string => {
  const backgroundColor = {
    error: colorUtils.red01,
    info: colorUtils.darkBlue01,
    confirm: colorUtils.green01,
    hint: colorUtils.darkBlue01,
  }[variant];

  const headlineColor = {
    error: colorUtils.red02,
    info: colorUtils.darkBlue02,
    confirm: colorUtils.green02,
    hint: colorUtils.darkBlue02,
  }[variant];

  const textColor = {
    error: colorUtils.red03,
    info: colorUtils.darkBlue02,
    confirm: colorUtils.green03,
    hint: colorUtils.darkBlue02,
  }[variant];

  const linkColor = {
    error: colorUtils.red02,
    info: colorUtils.darkBlue02,
    confirm: colorUtils.green02,
    hint: colorUtils.darkBlue02,
  }[variant];

  const linkHoverColor = {
    error: colorUtils.red03,
    info: colorUtils.darkBlue03,
    confirm: colorUtils.green03,
    hint: colorUtils.darkBlue03,
  }[variant];

  const styles = getCss({
    '@global': {
      ':host': {
        display: 'block',
        transition: 'height 250ms ease',
      },

      '.dc-ui-callout__container': {
        fontFamily: 'Bosch Sans',
        position: 'relative',
        display: 'grid',
        gridTemplateColumns: '1fr',
        backgroundColor: backgroundColor,
        padding: compact
          ? noIcon
            ? '0.6875rem 1rem'
            : '0.6875rem 1rem 0.6875rem 3.5rem'
          : noIcon
            ? '1rem'
            : '1rem 1rem 1rem 3.5rem',
        transition: 'opacity 250ms ease',
      },

      '.dc-ui-callout__icon': {
        display: 'flex',
        position: 'absolute',
        left: '1rem',
        top: compact ? '0.5rem' : '0.75rem',
      },

      '.dc-ui-callout__content': {
        display: 'grid',
        ...(compact
          ? {
              gridAutoFlow: 'column',
              gap: '1rem',
              justifyContent: 'start',
            }
          : {}),
      },

      '.dc-ui-callout__headline': {
        ...FULL_TEXT_STYLES['mid-size-text'].base,
        fontWeight: 'bold',
        color: headlineColor,
        lineHeight: '16px',
      },

      '.dc-ui-callout__headline + .dc-ui-callout__text': {
        marginTop: '0.5rem',
      },

      '.dc-ui-callout__text': {
        ...FULL_TEXT_STYLES['mid-size-text'].base,
        color: textColor,
        ...(compact && {
          whiteSpace: 'nowrap',
          overflow: 'hidden',
          textOverflow: 'ellipsis',
          lineHeight: '18px',
        }),
      },

      '.dc-ui-callout__text + .dc-ui-callout__link': {
        marginTop: compact ? '0' : '1rem',
      },

      '.dc-ui-callout__headline + .dc-ui-callout__link': {
        marginTop: compact ? '0' : '1rem',
      },

      '.dc-ui-callout__link': {
        display: 'inline-block',
        fontSize: '14px',
        lineHeight: compact ? '18px' : '24px',
        textDecoration: 'underline',
        justifySelf: 'start',

        '&:link, &:visited': {
          color: linkColor,
        },

        '&:hover, &:active': {
          color: linkHoverColor,
        },
      },
    },
  });

  return styles;
};
import { Component, Host, h, Prop, Element } from '@stencil/core';
import { injectCSS, injectFont } from '../../utils';
import { TIcon } from '../../svg/icons';
import { createStyles } from './callout-styles';
import { TCalloutVariant } from './callout-utils';

@Component({
  tag: 'dc-ui-callout',
  shadow: true,
})
export class DcUiCallout {
  @Element() el: HTMLElement;
  @Prop() headline = '';
  @Prop() variant: TCalloutVariant = 'error';
  @Prop() noIcon = false;
  @Prop() linkLabel = '';
  @Prop() linkHref = '';
  @Prop() shouldRemove = false;
  @Prop() removeDelay = 3;

  /**
   * Has no effect anymore
   * @deprecated since v0.0.8, will be removed with next major release
   */
  @Prop() inline = false;
  @Prop() compact = false;

  private container: HTMLDivElement;
  slotFilled: boolean;

  componentWillLoad() {
    injectFont();

    if (this.shouldRemove) {
      setTimeout(() => {
        this.removeSelf();
      }, this.removeDelay * 1000);
    }
  }

  componentWillRender() {
    this.slotFilled = !!this.el.innerHTML.trim();
  }

  removeSelf() {
    this.container.style.opacity = '0';
    this.el.style.height = `${this.el.offsetHeight}px`;

    this.container.addEventListener('transitionend', () => {
      this.el.style.height = '0';
      this.el.addEventListener('transitionend', () => {
        this.el.remove();
      });
    });
  }

  iconColor() {
    return {
      error: 'red02',
      info: 'darkBlue02',
      confirm: 'green02',
      hint: 'darkBlue02',
    }[this.variant];
  }

  iconName(): TIcon {
    return {
      error: 'dc_callout_error' as TIcon,
      info: 'dc_callout_information' as TIcon,
      confirm: 'dc_callout_confirmation' as TIcon,
      hint: 'dc_callout_hint' as TIcon,
    }[this.variant];
  }

  render() {
    injectCSS(this.el, createStyles, this.variant, this.compact, this.noIcon);

    return (
      <Host>
        <div
          class="dc-ui-callout__container"
          ref={(el) => (this.container = el)}>
          {!this.noIcon && (
            <div class="dc-ui-callout__icon">
              <dc-ui-icon
                name={this.iconName()}
                color={this.iconColor()}></dc-ui-icon>
            </div>
          )}

          <div class="dc-ui-callout__content">
            {this.headline && !this.compact && (
              <span class="dc-ui-callout__headline">{this.headline}</span>
            )}
            {this.slotFilled && (
              <span class="dc-ui-callout__text">
                <slot></slot>
              </span>
            )}
            {this.linkLabel && this.linkHref && (
              <a
                class="dc-ui-callout__link"
                href={this.linkHref}>
                {this.linkLabel}
              </a>
            )}
          </div>
        </div>
      </Host>
    );
  }
}
