@import '../dc-ui-link/dc-ui-link';

.dc-ui-link {
  font-family: 'Bosch Sans';
  font-weight: bold;
  font-size: 18px;
  line-height: 24px;

  &:hover {
    text-decoration: underline;
  }

  &.uppercase {
    text-transform: uppercase;
  }
}
import { Component, Host, h, Element, Prop, Watch } from '@stencil/core';
import { injectFont, parseAndGetAriaAttributes, SelectedAriaAttributes } from '../../utils';
import { TLinkAriaAttribute } from '../dc-ui-link/link-utils';

@Component({
  tag: 'dc-ui-product-link',
  styleUrl: './dc-ui-product-link.scss',
  shadow: true,
})
export class DcUiProductLink {
  @Element() el: HTMLElement;
  @Prop() href: string;
  @Prop() target?: string = '_self';
  @Prop() inline?: boolean = true;
  @Prop() aria?: SelectedAriaAttributes<TLinkAriaAttribute>;
  /* changed in v0.0.11: uppercase - default is false */
  @Prop() uppercase: boolean = false;
  @Prop() disabled?: boolean = false;
  @Prop() download?: boolean = false;

  @Watch('href')
  checkForHref() {
    if (!this.href || this.href === '') {
      this.disabled = true;
    }
  }

  componentWillLoad() {
    injectFont();
    this.checkForHref();
  }

  render() {
    const hrefAttribute = !this.disabled ? { href: this.href } : {};
    const downloadAttribute = this.download ? { download: '' } : {};
    const disabledAttribute = this.disabled ? { ariaDisabled: this.disabled, role: 'link' } : {};

    return (
      <Host
        class={`
            ${this.inline ? 'inline' : ''}
          `}>
        <a
          class={{
            'dc-ui-link': true,
            uppercase: this.uppercase,
            disabled: this.disabled,
          }}
          {...hrefAttribute}
          {...downloadAttribute}
          {...disabledAttribute}
          target={this.target}
          {...parseAndGetAriaAttributes(this.aria)}>
          <slot></slot>
        </a>
      </Host>
    );
  }
}
