import {
  buildSlottedStyles,
  getCss,
  getImageStyleJssStyle,
  getObjectFitJssStyle,
  getRatioJssStyle,
} from '../../utils';
import { TImageObjectFit, TImageStyle } from './image-utils';

export const createStyles = (): string => {
  return getCss({
    '@global': {
      ':host': {
        display: 'block',
      },
    },
  });
};

export const getSlottedCss = (
  el: HTMLElement,
  ratio: string,
  imageStyle: TImageStyle,
  objectFit: TImageObjectFit
): string => {
  return getCss(
    buildSlottedStyles(el, {
      '& img': {
        display: 'block',
        width: '100%',
        height: 'auto',
        ...getRatioJssStyle(ratio),
        ...getObjectFitJssStyle(objectFit),
        ...getImageStyleJssStyle(imageStyle),
      },
    })
  );
};
import { Component, h, Host, Prop, Element } from '@stencil/core';
import { attachSlottedCss, injectCSS } from '../../utils';
import { createStyles, getSlottedCss } from './image-styles';
import { TImageObjectFit, TImageStyle } from './image-utils';

@Component({
  tag: 'dc-ui-image-wrapper',
  shadow: true,
})
export class DcUiImageWrapper {
  @Element() el: HTMLElement;
  @Prop() imageStyle: TImageStyle;
  @Prop() objectFit: TImageObjectFit;
  @Prop() ratio?: string;

  public connectedCallback(): void {
    attachSlottedCss(this.el, getSlottedCss(this.el, this.ratio, this.imageStyle, this.objectFit));
  }

  render() {
    injectCSS(this.el, createStyles);

    return (
      <Host>
        <slot />
      </Host>
    );
  }
}
