import React, { useState, useEffect } from "react";

export default function Modal() {
  const [modal, setModal] = useState(false);

  const toggleModal = () => {
    setModal(!modal);
  };

  useEffect(() => {
    if (modal) {
      document.body.classList.add('active-modal');
      document.body.classList.add('disable-pointer-events');
    } else {
      document.body.classList.remove('active-modal');
      document.body.classList.remove('disable-pointer-events');
    }
  }, [modal]);

  const modalStyles = {
    modal: {
      width: '100vw',
      height: '100vh',
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      zIndex: 1000,
    },
    overlay: {
      background: 'rgba(49,49,49,0.8)',
    },
    modalContent: {
      position: 'absolute',
      top: '40%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      lineHeight: 1.4,
      background: '#f1f1f1',
      padding: '14px 28px',
      borderRadius: '3px',
      maxWidth: '600px',
      minWidth: '300px',
      zIndex: 1001,
    },
    closeModal: {
      position: 'absolute',
      top: '10px',
      right: '10px',
    },
    btnModal: {
      width: '250px', 
      height: '60px' 
    }
  };

  return (
    <>
    <dc-ui-button variant="primary" size="normal" type="button" align="center" icon="dc_download" onClick={toggleModal} style={modalStyles.btnModal}>
          <dc-ui-text tag="span" variant="small-text" color="inherit" text-align="center">download</dc-ui-text>
      </dc-ui-button>
      {modal && (
        <div style={{ ...modalStyles.modal, ...modalStyles.overlay }}>
          <div onClick={toggleModal} style={{ ...modalStyles.modal, ...modalStyles.overlay }}></div>
          <div style={modalStyles.modalContent}>
            <h2>Hello Modal</h2>
            <dc-ui-button variant="secondary" size="small" type="button" align="center" icon="dc_close" style={modalStyles.closeModal} onClick={toggleModal}>
           </dc-ui-button>
          </div>
        </div>
      )}
    </>
  );
}
