$tab-navigation-tab-height: 48px;
$tab-navigation-tab-gap: 12px;
$tab-navigation-tab-padding-x: 16px;
$tab-navigation-tab-padding-y: 12px;
$tab-navigation-tab-border-width-default: 1px;
$tab-navigation-tab-border-width-focused: 2px;
$tab-navigation-tab-border-width-selected: 8px;
$tab-navigation-content-padding-x: 16px;
$tab-navigation-content-padding-y: 32px;
$tab-navigation-sticky-offset: var(--sticky-offset, 0);

.dc-ui-tab-navigation-wrapper {
  position: relative;

  &.variant-dark {
    &::before {
      content: '';
      position: absolute;
      inset: $tab-navigation-tab-height 0 0;
      background-color: $color-dark-blue-02;
      box-shadow: 0 0 0 50vmax $color-dark-blue-02;
      clip-path: inset(0 -50vw);
    }
  }
}

.dc-ui-tab-navigation-tabs {
  position: absolute;
  inset: 0;
  height: $tab-navigation-tab-height;
  background-color: $color-white;
  box-shadow: inset 0 calc($tab-navigation-tab-border-width-default * -1) $color-grey-03;
  display: flex;
  gap: $tab-navigation-tab-gap;
  z-index: 1;
  overflow-x: scroll;
  overflow-y: hidden;
  scrollbar-width: none;
  -ms-overflow-style: none;

  &::-webkit-scrollbar {
    height: 0;
  }

  .variant-dark & {
    box-shadow: none;
  }

  &.sticky {
    position: fixed;
    top: $tab-navigation-sticky-offset;
    left: unset;
    right: unset;
    margin: 0 auto;
    z-index: 2;

    &::before {
      content: '';
      position: fixed;
      inset: $tab-navigation-sticky-offset 0 0;
      height: calc($tab-navigation-tab-height - $tab-navigation-tab-border-width-default);
      background-color: $color-white;

      .variant-dark & {
        height: $tab-navigation-tab-height;
      }
    }
  }
}

.dc-ui-tab-navigation-tab {
  position: relative;
  height: 100%;
  padding: $tab-navigation-tab-padding-y $tab-navigation-tab-padding-x;
  border: 0;
  outline: 0;
  text-transform: uppercase;
  font-weight: bold;
  user-select: none;
  white-space: nowrap;
  color: $color-grey-06;
  background-color: transparent;
  cursor: pointer;

  &[data-selected] {
    box-shadow: inset 0 calc($tab-navigation-tab-border-width-selected * -1) $color-turquoise-01;
    background-color: $color-grey-01;

    &:hover {
      background-color: $color-grey-02;
    }

    &:focus-visible {
      box-shadow:
        inset 0 0 0 $tab-navigation-tab-border-width-default $color-grey-01,
        inset 0
          calc(
            ($tab-navigation-tab-border-width-selected - $tab-navigation-tab-border-width-focused) *
              -1
          )
          $color-turquoise-01;

      &:hover {
        box-shadow:
          inset 0 0 0 $tab-navigation-tab-border-width-default $color-grey-02,
          inset 0
            calc(
              ($tab-navigation-tab-border-width-selected - $tab-navigation-tab-border-width-focused) *
                -1
            )
            $color-turquoise-01;
      }
    }
  }

  &[data-selected],
  &:hover {
    color: $color-dark-blue-02;
    font-weight: 900;
  }

  &:focus-visible {
    padding: calc($tab-navigation-tab-padding-y - $tab-navigation-tab-border-width-focused)
      calc($tab-navigation-tab-padding-x - $tab-navigation-tab-border-width-focused);
    border: $tab-navigation-tab-border-width-focused solid $color-turquoise-03;
  }

  &[disabled] {
    color: $color-grey-03;
    cursor: not-allowed;

    & > * {
      pointer-events: none;
    }
  }
}

.dc-ui-tab-navigation-content {
  position: relative;
  padding-top: calc($tab-navigation-tab-height + $tab-navigation-content-padding-y);

  .variant-dark & {
    padding-bottom: $tab-navigation-content-padding-y;
    color: $color-white;
  }
}
import { Component, Element, Host, h, Listen, Prop, State } from '@stencil/core';
import { DcUiTabNavigationItem } from '../dc-ui-tab-navigation-item/dc-ui-tab-navigation-item';
import { ITabNavigationTab, TTabNavigationVariant } from './tab-navigation-utils';

@Component({
  tag: 'dc-ui-tab-navigation',
  styleUrl: 'dc-ui-tab-navigation.scss',
  shadow: true,
})
export class DcUiTabNavigation {
  @Element() host: HTMLElement;
  tabsWrapper: HTMLElement;
  offsetElement: HTMLElement;

  @Prop() selectedTab: number = 0;
  @Prop() variant: TTabNavigationVariant = 'light';
  @Prop() sticky: boolean = false;
  @Prop() stickyOffset?: number;
  @Prop() stickyOffsetElement: string = '';

  @State() tabs: ITabNavigationTab[] = [];
  @State() stickyNavigation: boolean = false;
  @State() stickyOffsetValue: number = 0;

  stickinessObserver: IntersectionObserver;

  @Listen('changeTabItem')
  HandleChangeTabItem() {
    this.updateTabs();
  }

  componentWillLoad() {
    if (this.sticky && this.setStickyOffsetElement()) {
      document.addEventListener('scroll', this.onScroll);

      const stickyOffsetElementObserver: MutationObserver = new MutationObserver(
        this.updateStickyOffset
      );
      stickyOffsetElementObserver.observe(this.offsetElement, {
        attributes: true,
        characterData: true,
        childList: true,
        subtree: true,
      });
    }

    this.updateTabItems();

    window.addEventListener('resize', this.onResize);
  }

  componentDidRender() {
    if (this.sticky) {
      this.host.style.setProperty('--sticky-offset', `${this.stickyOffsetValue}px`);

      this.stickinessObserver?.disconnect();
      this.stickinessObserver = new IntersectionObserver(
        ([entry]: [IntersectionObserverEntry]): void => {
          this.stickyNavigation = entry.isIntersecting;
          this.updateStickyOffset();
          this.updateTabsWidth();
        },
        {
          rootMargin: `${this.stickyOffsetValue * -1}px 0px ${this.stickyOffsetValue - document.documentElement.clientHeight}px`,
        }
      );
      this.stickinessObserver.observe(this.host);
    }
  }

  disconnectedCallback() {
    document.removeEventListener('scroll', this.onScroll);
    window.removeEventListener('resize', this.onResize);
  }

  private onScroll = (): void => {
    this.updateStickyOffset();
  };

  private onResize = (): void => {
    if (this.sticky) this.updateStickyOffset();
    this.updateTabsWidth();
  };

  private setStickyOffsetElement = (): boolean => {
    this.offsetElement = document.querySelector(this.stickyOffsetElement || 'body header');
    if (!this.offsetElement) {
      console.warn(
        this.host,
        'Could not find sticky offset element. Please provide a valid selector. Offset 0 used as fallback.'
      );
      return false;
    }
    return true;
  };

  private getStickyOffsetElementBottom = (): number => {
    if (
      !this.offsetElement?.checkVisibility({ checkOpacity: true, checkVisibilityCSS: true }) ||
      this.offsetElement?.hasAttribute('hidden')
    ) {
      return 0;
    }
    return Math.max(0, this.offsetElement?.getBoundingClientRect().bottom);
  };

  private updateStickyOffset = (): void => {
    this.stickyOffsetValue = this.stickyOffset || this.getStickyOffsetElementBottom();
    this.host.style.setProperty('--sticky-offset', `${this.stickyOffsetValue}px`);
  };

  private updateTabs = (): void => {
    const tabs = [];
    Array.from(this.host.children).forEach((item: HTMLElement & DcUiTabNavigationItem) => {
      tabs.push({
        label: item.getAttribute('label'),
        disabled: item.hasAttribute('disabled'),
      } satisfies ITabNavigationTab);
    });
    this.tabs = tabs;
  };

  private updateTabItems = (): void => {
    if (this.selectedTab < 0 || this.selectedTab >= this.host.children.length) this.selectedTab = 0;

    Array.from(this.host.children).forEach(
      (item: HTMLElement & DcUiTabNavigationItem, index: number) => {
        item.toggleAttribute('active', this.selectedTab === index);
      }
    );
  };

  private updateTabsWidth = (): void => {
    this.tabsWrapper.style.width = `${this.tabsWrapper.parentElement.offsetWidth}px`;
  };

  private onTabChange = (index: number): void => {
    this.selectedTab = index;
    this.updateTabItems();

    window.dispatchEvent(new Event('resize'));

    // scroll to top of component with offset in mind. Only if stickyNavigation is active
    if (this.stickyNavigation) {
      window.scrollTo({
        top: this.host.getBoundingClientRect().top + window.scrollY - this.stickyOffsetValue,
      });
    }
  };

  render() {
    return (
      <Host>
        <div class={`dc-ui-tab-navigation-wrapper variant-${this.variant}`}>
          <div
            ref={(el) => (this.tabsWrapper = el)}
            class={{
              'dc-ui-tab-navigation-tabs': true,
              sticky: this.stickyNavigation,
            }}>
            {this.tabs.map((tab: ITabNavigationTab, index: number) => (
              <button
                class="dc-ui-tab-navigation-tab"
                disabled={tab.disabled}
                data-selected={this.selectedTab === index}
                onClick={() => {
                  this.onTabChange(index);
                }}>
                <dc-ui-text weight="inherit">{tab.label}</dc-ui-text>
              </button>
            ))}
          </div>
          <div class="dc-ui-tab-navigation-content">
            <slot onSlotchange={this.updateTabs} />
          </div>
        </div>
      </Host>
    );
  }
}
