import { getCss, color as colorUtils } from '../../utils';

export const createStyles = (
  direction: 'left' | 'right',
  zIndex: number,
  fixedWidth: boolean
): string => {
  return getCss({
    '@global': {
      ':host': {
        display: 'inline-block',
        position: 'relative',
      },
      ul: {
        listStyle: 'none',
        margin: '0',
        padding: '0',
        maxWidth: fixedWidth ? '176px' : 'auto',
      },
      '.dc-ui-context__trigger': {
        position: 'relative',

        '&:before': {
          content: '""',
          position: 'absolute',
          zIndex: 3,
          bottom: '0',
          left: '0',
          right: '0',
          height: '4px',
          backgroundColor: colorUtils.white,
          display: 'none',
        },

        '&--open': {
          boxShadow: '0 2px 6px 0 rgba(0,0,0,0.3)',

          '&:before': {
            display: 'block',
          },
        },
      },
      '.dc-ui-context__content': {
        display: 'none',
        position: 'absolute',
        flexDirection: 'column',
        backgroundColor: colorUtils.white,
        boxShadow: '0 2px 6px 0 rgba(0,0,0,0.3)',
        top: '100%',
        width: 'max-content',
        minWidth: '100%',
        zIndex: zIndex,
        [direction]: '0',

        '&--open': {
          display: 'inline-block',
        },
      },
    },
  });
};
import { injectCSS } from '../../utils';
import { createStyles } from './context-styles';
import { DcUiContext } from './dc-ui-context';

export const CONTEXT_DIRECTIONS = ['right', 'left'] as const;
export type TContextDirectionType = typeof CONTEXT_DIRECTIONS[number];

// space between the context menu and the edge of the screen
const safeZonePx = 16;

// position the context menu
const getDocumentHeightWidthWithoutSafeZone = (): {
  clientWidth: number;
  clientHeight: number;
} => {
  const { clientWidth, clientHeight } = document.documentElement;
  return { clientWidth: clientWidth - safeZonePx, clientHeight: clientHeight - safeZonePx };
};

const getPositionAndWidthOfElement = (el: HTMLElement): { x: number; y: number; width: number } => {
  const { x, y, width } = el.getBoundingClientRect();
  return { x, y, width };
};

const checkIfElementIsOutsideOfViewport = (el: HTMLElement): boolean => {
  const { clientWidth } = getDocumentHeightWidthWithoutSafeZone();
  const { x, width } = getPositionAndWidthOfElement(el);
  const isOutsideOfViewport = x + width > clientWidth;
  return isOutsideOfViewport;
};

export const updateContextPosition = (
  host: HTMLElement,
  context: HTMLElement,
  zIndex: number,
  direction: TContextDirectionType,
  fixedWidth: boolean
): void => {
  if (checkIfElementIsOutsideOfViewport(context)) {
    const newDirection = direction === 'left' ? 'right' : 'left';
    injectCSS(host, createStyles, newDirection, zIndex, fixedWidth);
  }
};

// event handling
export const registeredContextMenus: DcUiContext[] = [];

export const addDocumentEventListener = (context: DcUiContext): void => {
  if (!registeredContextMenus.includes(context)) {
    registeredContextMenus.push(context);
    document.addEventListener('mousedown', onDocumentMousedown);
    document.addEventListener('keydown', onDocumentKeydown);
  }
};

// Removes the event listeners for the specified context menu.
// If there are no remaining context menus, the document event listeners are also removed.
// This function should only be called from the destroy function of the context menu.
export const removeDocumentEventListener = (context: DcUiContext): void => {
  const contextIndex = registeredContextMenus.indexOf(context);
  if (contextIndex > -1) {
    registeredContextMenus.splice(contextIndex, 1);
  }
  if (registeredContextMenus.length === 0) {
    document.removeEventListener('mousedown', onDocumentMousedown);
    document.removeEventListener('keydown', onDocumentKeydown);
  }
};

export const onDocumentMousedown = (e: MouseEvent): void => {
  const context = registeredContextMenus.find(
    // eslint-disable-next-line dot-notation
    (contextElement) => contextElement['open'] && !e.composedPath().includes(contextElement.el)
  );
  if (context) {
    // eslint-disable-next-line dot-notation
    context['open'] = false;
  }
};

export const onDocumentKeydown = (e: KeyboardEvent): void => {
  const { key } = e;
  const isEscape = key === 'Escape';
  if (isEscape || ['SpaceBar', 'Enter', ' '].includes(key)) {
    const context = registeredContextMenus.find(
      // eslint-disable-next-line dot-notation
      (contextElement) =>
        contextElement['open'] && (isEscape || !e.composedPath().includes(contextElement.el))
    );
    if (context) {
      // eslint-disable-next-line dot-notation
      context['open'] = false;
    }
  }
};
import { Component, h, Host, Prop, Element, Method } from '@stencil/core';
import { TButtonSize } from '../dc-ui-button/button-utils';
import { TIcon } from '../../svg/icons';
import { injectCSS } from '../../utils';
import { createStyles } from './context-styles';
import {
  addDocumentEventListener,
  updateContextPosition,
  removeDocumentEventListener,
  TContextDirectionType,
} from './context-utils';

@Component({
  tag: 'dc-ui-context',
  shadow: true,
})
export class DcUiContext {
  @Element() el: HTMLElement;
  @Prop() triggerLabel?: string;
  @Prop() hideLabel = false;
  @Prop() triggerSize: TButtonSize = 'small';
  @Prop() icon: TIcon = 'dc_options';
  @Prop() fixedWidth = true;
  @Prop() disabled = false;
  @Prop() zIndex?: number = 2;
  @Prop() open = false;

  private direction: TContextDirectionType = 'left';
  context: HTMLUListElement;

  @Method() async closeMenu() {
    this.open = false;
  }

  @Method() async openMenu() {
    this.open = true;
  }

  toggleMenu = (): void => {
    this.open = !this.open;
  };

  connectedCallback(): void {
    addDocumentEventListener(this);
  }

  public componentDidRender(): void {
    if (this.open) {
      updateContextPosition(this.el, this.context, this.zIndex, this.direction, this.fixedWidth);
    }
  }

  public disconnectedCallback(): void {
    removeDocumentEventListener(this);
  }

  handleClick = (): void => {
    if (this.disabled) {
      return;
    }

    this.toggleMenu();
  };

  render() {
    injectCSS(this.el, createStyles, this.direction, this.zIndex, this.fixedWidth);

    return (
      <Host>
        <dc-ui-button
          disabled={this.disabled}
          size={this.triggerSize}
          variant="link"
          hide-label={this.hideLabel || this.triggerLabel === undefined}
          onClick={this.handleClick}
          id="context-trigger"
          class={{
            'dc-ui-context__trigger': true,
            'dc-ui-context__trigger--open': this.open,
          }}
          aria={`{ 'aria-expanded': ${this.open},  'aria-controls': 'context' }`}
          icon={this.icon}>
          {this.triggerLabel}
        </dc-ui-button>
        <ul
          ref={(el) => (this.context = el)}
          id="context"
          aria-labelledby="context-trigger"
          class={{
            'dc-ui-context__content': true,
            'dc-ui-context__content--open': this.open,
          }}>
          <slot />
        </ul>
      </Host>
    );
  }
}
