import { getCss, buildResponsiveStyles, mergeDeep, color } from '../../utils';
import {
  TDividerDirection,
  DividerDirectionType,
  TDividerSize,
  DividerSizeType,
} from './divider-utils';

export const createStyles = (
  direction: TDividerDirection,
  size: TDividerSize,
  isInsideBtnGroup: boolean
): string => {
  const elementSpace = isInsideBtnGroup ? '0' : '8px';

  return getCss({
    '@global': {
      ':host': buildResponsiveStyles(direction, (directionResponsive: DividerDirectionType) => ({
        display: directionResponsive === 'horizontal' ? 'block' : 'inline-block',
        width: directionResponsive === 'horizontal' ? '100%' : 'auto',
        height: directionResponsive === 'horizontal' ? 'auto' : 'auto',
      })),
      hr: {
        border: 'none',
        backgroundColor: color.grey03,
        ...mergeDeep(
          buildResponsiveStyles(direction, (directionResponsive: DividerDirectionType) => ({
            display: directionResponsive === 'horizontal' ? 'block' : 'inline-block',
            margin:
              directionResponsive === 'horizontal' ? `${elementSpace} 0` : `0 ${elementSpace}`,
            ...buildResponsiveStyles(size, (sizeResponsive: DividerSizeType) => ({
              height: directionResponsive === 'horizontal' ? `${sizeResponsive}px` : '100%',
              width: directionResponsive === 'horizontal' ? '100%' : `${sizeResponsive}px`,
            })),
          }))
        ),
      },
    },
  });
};
import { Component, h, Prop, Element } from '@stencil/core';
import { injectCSS } from '../../utils';
import { createStyles } from './divider-styles';
import { TDividerDirection, TDividerSize } from './divider-utils';

@Component({
  tag: 'dc-ui-divider',
  shadow: true,
})
export class DcUiDivider {
  @Element() el: HTMLElement;
  @Prop() width: TDividerSize = 1;
  @Prop() direction: TDividerDirection = 'horizontal';

  isInsideBtnGroup: boolean;

  componentWillLoad() {
    this.isInsideBtnGroup = this.el.parentElement.tagName === 'DC-UI-BTN-GROUP';
  }

  render() {
    injectCSS(this.el, createStyles, this.direction, this.width, this.isInsideBtnGroup);

    return <hr />;
  }
}
