import React, { useState, useEffect, useRef } from 'react';
import Preview3DApi from './Preview3D/Preview3DApi';
import WebViewer3D from './Preview3D/WebViewer3D';
import Preview2DApi from './Preview2D/Preview2DApi';
import Modal from './DownloadModals/Modal';
import Footer from './Footer/Footer';

import { getCytropacValues, getCytropacMiDent, computeCytropacTypecodeInfo, generateCytropacTabledata } from './Products/cytropac';
import { getCytroboxValues, getCytroboxMiDent, computeCytroboxTypecodeInfo, generateCytroboxTabledata } from './Products/cytrobox';


import { defineCustomElements } from '@boschrexroth/nextgen-web-ui-toolkit-react';
defineCustomElements();

const TableDataApi = ({ initialFilepath }) => {
  const [data, setData] = useState(null);
  const [apiData, setApiData] = useState(null);
  const [miDent, setMiDent] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('english');
  const [selectedValues, setSelectedValues] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [typecodeInfo,setTypecodeInfo] = useState('')
  // const [error, setError] = useState(null);
  const [filepath, setFilepath] = useState(initialFilepath);
  const [prefix, setPrefix] = useState('');
  // const [suggestions, setSuggestions] = useState([]);
  const inputRef = useRef(null);
  const suggestionsRef = useRef(null);

  const handleApiData = (data) => {
    setApiData(data);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const params = {
          apikey: 'e66120f9d9624823884cac1bf290ea88',
          language: selectedLanguage,
          eol: '1',
          plm: 'true',
          includeunclassifiedbycountry: 'false', 
          enablePreviewPerLine: 'true',
          path: filepath,
          includecrossref: 'true',
        };
        const queryString = new URLSearchParams(params).toString();
        const url = `https://webapi.partcommunity.com/service/table/vertical?${queryString}`;
        const response = await fetch(url);
        const result = await response.json();
        setData(result);
        const { NN } = result.index;

        if (NN === 'CytroBox') {
          const values = getCytroboxValues(result);
          setSelectedValues(values);
          setTypecodeInfo(computeCytroboxTypecodeInfo(values));
        } else if (NN === 'CytroPac') {
          const values = getCytropacValues(result);
          setSelectedValues(values);
          setTypecodeInfo(computeCytropacTypecodeInfo(values));
        }
      } catch (error) {
        console.error('Error fetching the data', error);
      }
    };
    fetchData();
  }, [selectedLanguage],[filepath]);

  useEffect(() => {
    updateMiDent();
  });

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        inputRef.current && 
        !inputRef.current.contains(event.target) && 
        suggestionsRef.current && 
        !suggestionsRef.current.contains(event.target)
      ) {
        setSuggestions([]);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const updateMiDent = () => {
    if (data && data.index && data.index.NN === 'CytroBox') {
      setMiDent(getCytroboxMiDent(selectedValues));
    } else if (data && data.index && data.index.NN === 'CytroPac') {
      setMiDent(getCytropacMiDent(selectedValues));
    }
  };

  // const handleChange = (field, value) => {
  //   setSelectedValues(prevValues => ({
  //     ...prevValues,
  //     [field]: value
  //   }));
  // };

  const handleChange = (field, value) => {
    setSelectedValues(prevValues => {
      const newValues = { ...prevValues, [field]: value };
      const { NN } = data?.index || {};

      if (NN === 'CytroBox') {
        setTypecodeInfo(computeCytroboxTypecodeInfo(newValues));
      } else if (NN === 'CytroPac') {
        setTypecodeInfo(computeCytropacTypecodeInfo(newValues));
      }

      return newValues;
    });
  };

  const handleLanguageChange = (event) => {
    setSelectedLanguage(event.target.value);
  };

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleInputChange = (event) => {
    setTypecodeInfo(event.target.value);
  };

  const handleBlur = () => {
    setIsEditing(false);
    // You can add any save functionality here if needed
  };

  const handlePrefixChange = (event) => {
    const newPrefix = event.target.value;
    setPrefix(newPrefix);
    updateSuggestions(newPrefix);
  };

  const handleKeyDown = (event) => {
    if (event.key === 'Enter') {
      setIsEditing(false);
      if (prefix.toLowerCase() === 'cytrobox') {
        setFilepath('bosch_rexroth_mcd/industrial_hydraulics/power_units/std_power_units/cytrobox_asmtab.prj');
      } else if (prefix.toLowerCase() === 'cytropac') {
        setFilepath('bosch_rexroth_mcd/industrial_hydraulics/power_units/std_power_units/cytropac_asmtab.prj');
      }
    }
  };

  const updateSuggestions = (input) => {
    const availablePrefixes = ['cytrobox', 'cytropac'];
    const filteredSuggestions = availablePrefixes.filter(prefix =>
      prefix.toLowerCase().startsWith(input.toLowerCase())
    );
    setSuggestions(filteredSuggestions);
  };

  if (!data) {
    return <div>Api error....</div>;
  }

  const indexPath = data.index.line.values;

  const renderRow = (label, values, fieldName, units) => (
    <tr key={fieldName}>
      <td>{label}</td>
      <td>
        {values.length === 1 ? (
          values[0].name
        ) : (
          <dc-ui-dropdown-wrapper>
            <select onChange={(e) => handleChange(fieldName, e.target.value)}>
              {values.map((item, index) => (
                <option key={index} value={item.value}>{item.name}</option>
              ))}
            </select>
          </dc-ui-dropdown-wrapper>
        )}
      </td>
      <td>{units}</td>
    </tr>
  );
  // let TypecodeInfo = '';
  // let Tabledata = null;

  // if (data.index.NN === 'CytroBox') {
  //   TypecodeInfo = computeCytroboxTypecodeInfo(selectedValues);
  //   Tabledata = generateCytroboxTabledata(indexPath, renderRow);
  // } else if (data.index.NN === 'CytroPac') {
  //   TypecodeInfo = computeCytropacTypecodeInfo(selectedValues);
  //   Tabledata = generateCytropacTabledata(indexPath, renderRow);
  // }

  const { NN } = data.index;
  const Tabledata = NN === 'CytroBox' 
    ? generateCytroboxTabledata(indexPath, renderRow) 
    : generateCytropacTabledata(indexPath, renderRow);


  const HeaderName = data.index.NT +' '+  data.index.NN;
  return (
    <div>
      <div>
        <div style={{ backgroundColor: "#ffffff" , paddingRight: "1%"}}>
          <div style={{ width: "15%", marginLeft: "auto", display: "block", backgroundColor: "#ffffff" }}>
            <dc-ui-dropdown-wrapper>
              <select value={selectedLanguage} onChange={handleLanguageChange}>
                <option value="english">English</option>
                <option value="german">German</option>
              </select>
            </dc-ui-dropdown-wrapper>
          </div>
        </div>
        <div style={{ backgroundColor: "#ffffff", display: "flex", alignItems: "center" }}>
          <dc-ui-image src='boschrexrothlogo.png' alt="Bosch Rexroth Logo" style={{ height: "4%",width: "8%", marginLeft: "2%", paddingTop: "0.5%", paddingBottom: "0.5%" }}/>
          <dc-ui-button variant="link" size="small" type="submit" style={{ marginLeft: "auto", paddingTop: "0.8%" }}>
            <dc-ui-icon name="dc_customerservice" size="small" align="center" /><br />
            Contact
          </dc-ui-button>
          <dc-ui-button variant="link" size="small" type="submit" style={{ paddingTop: "0.8%" }}>
            <dc-ui-icon name="dc_user" size="small" align="center" /><br />
            myrexroth
          </dc-ui-button>
          <dc-ui-button variant="link" size="small" type="submit" style={{ paddingTop: "0.8%" }}>
            <dc-ui-icon name="dc_cart" size="small" align="center" /><br />
            shopping cart
          </dc-ui-button>
        </div>
        <div style={{ background: "linear-gradient(to bottom, #cad6dd, white)", display: "flex", alignItems: "center", height: "10vh", padding: "0 1%" }}>
          <dc-ui-text variant="h4" color="inherit" weight="bold" text-align="left"
            style={{ marginLeft: "1%", whiteSpace: "nowrap", overflow: "hidden", textOverflow: "ellipsis", flex: "1" }}>
            {HeaderName}
          </dc-ui-text>
          <dc-ui-button variant="secondary" size="small" type="submit" style={{ marginLeft: "auto" }}>
            Consultation request
          </dc-ui-button>
        </div>
        <div style={{paddingLeft: "1%", paddingRight: "1%"}}>
          <div style={{ display: 'flex', alignItems: 'center', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.4)',  backgroundColor: '#FFFFFF'}}>
            <div style={{ backgroundColor: '#a3bac8', padding: '0.5%', textAlign: 'center', marginRight: '2%'}}>
              <dc-ui-text tag="span" variant="h5" color= "darkBlue02" weight="normal" text-align="center">
                Type code
              </dc-ui-text>
            </div>
            {/* <dc-ui-text tag="span" variant="h5" color="inherit" weight="bold" text-align="center" style={{ marginRight: '2%' }}>
              {TypecodeInfo}
            </dc-ui-text> */}
            {/* {isEditing ? (
              <input
                type="text"
                value={TypecodeInfo}
                onChange={handleInputChange}
                onBlur={handleBlur}
                autoFocus
                style={{ marginRight: '2%' }}
              />
            ) : (
              <dc-ui-text tag="span" variant="h5" color="inherit" weight="bold" text-align="center" style={{ marginRight: '2%' }}>
                {TypecodeInfo}
              </dc-ui-text>
            )}
            <dc-ui-button variant="link" size="small" type="button" onClick={handleEditClick}>
              <dc-ui-icon name="dc_edit" size="small" align="center" />
            </dc-ui-button> */}
            {isEditing ? (
            <dc-ui-text tag="span" variant="h5" color="inherit" weight="bold" text-align="center" style={{ marginRight: '2%' }}>
              <input
              type="text"
              placeholder="Enter prefix (cytrobox/cytropac)"
              value={prefix}
              onChange={handlePrefixChange}
              onKeyDown={handleKeyDown}
            />
            </dc-ui-text>
          ) : (
            <dc-ui-text tag="span" variant="h5" color="inherit" weight="bold" text-align="center" style={{ marginRight: '2%' }}>
              {typecodeInfo}
            </dc-ui-text>
          )}
          <dc-ui-button variant="link" size="small" type="button" onClick={handleEditClick}>
            <dc-ui-icon name="dc_edit" size="small" align="center" />
          </dc-ui-button>
          </div>
        </div>
        <div style={{ display: 'flex', alignItems: 'flex-start',paddingLeft: "1%", paddingRight: "1%" }}>
          <div style={{ flex: 1 }}>
            <center>
              <h3>Product 3D View</h3>
            </center>
            <Preview3DApi miDent={miDent} onDataFetch={handleApiData} />
            <WebViewer3D apiData={apiData} />
            <div style={{ marginLeft: '30%', paddingTop: '5%' }}>
              <Modal />
            </div>
          </div>
          <div style={{ flex: 1, marginLeft: '1.7%' }}>
            <center>
              <h3>Product 2D View</h3>
            </center>
            <Preview2DApi filepath = {filepath}/>
          </div>

          <div style={{ flex: 1}}>
            <center>
              <h3>Product Details</h3>
            </center>
            <div style={{ marginRight: "0vh", marginLeft: "auto", width: "80vh", overflow: "hidden", border: "2px solid rgba(0, 0, 0, 0.6)", padding: "1vh" }}>
              <dc-ui-table>
                <table>
                  {Tabledata}
                </table>
              </dc-ui-table>
            </div>
          </div>
        </div>
        </div>
        <Footer />
    </div>
  );
};

export default TableDataApi;
