import { getCss, getImageStyleJssStyle, getObjectFitJssStyle, getRatioJssStyle } from '../../utils';
import { TImageObjectFit, TImageStyle } from './image-utils';

export const createStyles = (
  ratio: string,
  imageStyle: TImageStyle,
  objectFit: TImageObjectFit
): string => {
  return getCss({
    '@global': {
      ':host': {
        display: 'block',
      },
      img: {
        display: 'block',
        width: '100%',
        height: 'auto',
        ...getRatioJssStyle(ratio),
        ...getObjectFitJssStyle(objectFit),
        ...getImageStyleJssStyle(imageStyle),
      },
      '.hidden': {
        position: 'absolute',
        height: '1px',
        width: '1px',
        opacity: 0,
      },
    },
  });
};
import { Component, h, Element, Prop, Host } from '@stencil/core';
import { TImageLoading, TImageObjectFit, TImageStyle } from './image-utils';
import { injectCSS } from '../../utils';
import { createStyles } from './image-styles';

@Component({
  tag: 'dc-ui-image',
  shadow: true,
})
export class DcUiImage {
  @Element() el: HTMLElement;
  @Prop() loading: TImageLoading = 'lazy';
  @Prop() alt?: string;
  @Prop() src: string;
  @Prop() fallbackSrc?: string;
  @Prop() ratio?: string;
  @Prop() imageStyle: TImageStyle = 'default';
  @Prop() objectFit: TImageObjectFit;

  render() {
    injectCSS(this.el, createStyles, this.ratio, this.imageStyle, this.objectFit);

    return (
      <Host>
        <img
          id="fallback"
          src={this.fallbackSrc || this.src}
          alt={this.alt}
          loading={this.loading}
        />
        {this.fallbackSrc && this.src && (
          <img
            class="hidden"
            src={this.src}
            onLoad={(e) => {
              this.el.shadowRoot.getElementById('fallback').setAttribute('src', this.src);
              (e.target as HTMLImageElement).remove();
            }}
            loading={this.loading}
          />
        )}
      </Host>
    );
  }
}
