import { getCss, color as colorUtils } from '../../utils';

export const createStyles = (disabled: boolean, ellipsis: boolean): string => {
  const backgroundColor = disabled ? colorUtils.darkBlue01 : colorUtils.darkBlue02;
  const color = disabled ? colorUtils.grey03 : colorUtils.white;

  const ellipsisHostStyles = ellipsis
    ? {
        maxWidth: '100%',
      }
    : {};

  const ellipsisTextStyles = ellipsis
    ? {
        maxWidth: '100%',
        overflow: 'hidden',
        textOverflow: 'ellipsis',
        whiteSpace: 'nowrap',
      }
    : {};

  const styles = getCss({
    '@global': {
      ':host': {
        position: 'relative',
        display: 'inline-flex',
        alignItems: 'center',
        borderRadius: '4px',
        backgroundColor: backgroundColor,
        padding: '6px 36px 6px 8px',
        boxSizing: 'border-box',
        color: color,
        ...ellipsisHostStyles,
      },

      'dc-ui-text': {
        ...ellipsisTextStyles,
        lineHeight: '16px',
      },

      button: {
        position: 'absolute',
        inset: '0 0 0 auto',
        width: '32px',
        display: 'inline-grid',
        placeItems: 'center',
        border: 'none',
        background: 'none',
        padding: '0',
        color: color,

        '&:not(:disabled)': {
          cursor: 'pointer',

          '&:hover': {
            backgroundColor: colorUtils.darkBlue03,
            borderRadius: '0 4px 4px 0',
          },

          '&:focus': {
            outline: '0',

            '&::before': {
              content: '""',
              display: 'block',
              position: 'absolute',
              inset: 0,
              border: `2px solid ${colorUtils.turquoise02}`,
              borderRadius: '0 4px 4px 0',
            },
          },
        },
      },
    },
  });

  return styles;
};
import { Component, Host, h, Prop, Element, Event, EventEmitter } from '@stencil/core';
import { injectCSS } from '../../utils';
import { createStyles } from './filter-tag-styles';

@Component({
  tag: 'dc-ui-filter-tag',
  shadow: true,
})
export class DcUiFilterTag {
  @Element() el: HTMLElement;
  @Prop() label: string;
  @Prop() disabled = false;
  @Prop() ellipsis = false;

  /** Emitted when close button is clicked. */
  @Event({ bubbles: false }) public closeButtonClicked: EventEmitter;

  private onCloseButtonClicked = (): void => {
    this.closeButtonClicked.emit();
  };

  render() {
    injectCSS(this.el, createStyles, this.disabled, this.ellipsis);

    return (
      <Host
        aria-disabled={this.disabled ? 'true' : null}
        title={this.ellipsis ? this.label : null}>
        <dc-ui-text variant="small-text">{this.label}</dc-ui-text>
        <button
          onClick={this.onCloseButtonClicked}
          disabled={this.disabled}>
          <dc-ui-icon
            name="dc_close"
            size="small"></dc-ui-icon>
        </button>
      </Host>
    );
  }
}
