import { getCss, color as colorUtils } from '../../utils';
import {TContextItemDividerPosition} from "./context-item-utils";

export const createStyles = (
  divider: TContextItemDividerPosition,
): string => {

  const border = `1px solid ${colorUtils.grey03}`;

  return getCss({
    '@global': {
      ':host': {
        display: 'block',
      },
      '.context-item': {
        appearance: 'none',
        WebkitAppearance: 'none', // iOS safari
        display: 'flex',
        alignItems: 'center',
        border: 'none',
        ...(divider === 'top' || divider === 'both' ? {borderTop: border} : {}),
        ...(divider === 'bottom' || divider === 'both' ? {borderBottom: border} : {}),
        textDecoration: 'none',
        padding: '0 6px',
        margin: '0',
        backgroundColor: 'transparent',
        cursor: 'pointer',
        width: '100%',
        height: '32px',
        textTransform: 'uppercase',
        color: colorUtils.grey06,
        position: 'relative',
        outline: 'none',
        boxSizing: 'border-box',

        '&:hover': {
          backgroundColor: colorUtils.grey01,
        },

        '&:focus': {
          '&:before': {
            content: '""',
            position: 'absolute',
            inset: '0',
            border: `2px solid ${colorUtils.turquoise03}`,
            zIndex: 3,
          },
        },

        '&--content': {
          display: 'grid',
          gridAutoFlow: 'column',
          alignItems: 'center',
          justifyContent: 'start',
          gap: '10px',

          '& > *': {
            flexShrink: 0,
          },

          '& > dc-ui-text': {
            whiteSpace: 'nowrap',
            overflow: 'hidden',
            textOverflow: 'ellipsis',
            lineHeight: '16px',
          },
        },
      },
    },
  });
};
import {Component, h, Prop, Element, Host} from '@stencil/core';
import {TIcon} from '../../svg/icons';
import {injectCSS} from '../../utils';
import {createStyles} from './context-item-styles';
import {TContextItemDividerPosition} from "./context-item-utils";

@Component({
  tag: 'dc-ui-context-item',
  shadow: true,
})
export class DcUiContextItem {
  @Element() el: HTMLElement;
  @Prop() label: string;
  @Prop() icon?: TIcon;
  @Prop() href?: string;
  @Prop() divider?: TContextItemDividerPosition;

  private parentContext: HTMLDcUiContextElement;

  connectedCallback(): void {
    this.parentContext = this.el.closest('dc-ui-context');
  }

  handleClick = (): void => {
    if (this.parentContext) {
      this.parentContext.closeMenu();
    }
  };

  render() {
    injectCSS(this.el, createStyles, this.divider);

    const content = (
      <div class="context-item--content">
        {this.icon && (
          <dc-ui-icon
            aria-hidden="true"
            name={this.icon}
          />
        )}
        <dc-ui-text variant="btn-small">{this.label}</dc-ui-text>
      </div>
    );

    return (
      <Host>
        <li
          onClick={this.handleClick}
          title={this.label}>
          {this.href ? (
            <a
              href={this.href}
              class="context-item">
              {content}
            </a>
          ) : (
            <button
              type="button"
              class="context-item">
              {content}
            </button>
          )}
        </li>
      </Host>
    );
  }
}
