:host {
  --switch-icon-color-on: #{$color-white};
  --switch-icon-color-off: #{$color-white};

  --switch-enabled-background-on: #{$color-green-02};
  --switch-enabled-background-off: #{$color-grey-05};

  --switch-hover-background-on: #42682b;
  --switch-hover-background-off: #{$color-grey-06};

  --switch-disabled-background-on: #{$color-green-01};
  --switch-disabled-background-off: #{$color-grey-02};

  --switch-disabled-icon-color-on: #b9ccae;
  --switch-disabled-icon-color-off: #{$color-grey-03};
}

:host {
  display: inline-block;
}

.dc-switch {
  display: grid;
  grid-auto-flow: row;
  gap: 8px;
  margin-bottom: 16px;

  input {
    position: absolute;
    opacity: 0;
    width: 0;
    height: 0;
  }

  &-slider {
    position: relative;
    width: 48px;
    height: 24px;
    cursor: pointer;
    background-color: var(--switch-enabled-background-off);
    transition: background-color 0.4s;
    border-radius: 34px;

    &:before {
      position: absolute;
      content: '';
      height: 18px;
      width: 18px;
      left: 3px;
      bottom: 3px;
      background-color: var(--switch-icon-color-off);
      transition: 0.4s;
      border-radius: 50%;
    }

    #on-icon,
    #off-icon {
      fill: var(--switch-icon-color-off);
      position: absolute;
      top: 50%;
      transition:
        opacity 0.4s,
        transform 0.4s;
    }

    #on-icon {
      left: 6px;
      opacity: 0;
      transform: translate(-2px, -50%);
    }

    #off-icon {
      right: 6px;
      opacity: 1;
      transform: translate(0, -50%);
    }

    &:hover {
      background-color: var(--switch-hover-background-off);
    }
  }

  input:checked + &-slider {
    background-color: var(--switch-enabled-background-on);

    &:before {
      transform: translateX(24px);
    }

    #on-icon {
      left: 6px;
      opacity: 1;
      transform: translate(0, -50%);
    }

    #off-icon {
      right: 6px;
      opacity: 0;
      transform: translate(2px, -50%);
    }

    &:hover {
      background-color: var(--switch-hover-background-on);
    }
  }

  input:disabled + &-slider {
    cursor: not-allowed;

    background-color: var(--switch-disabled-background-off);

    &:before {
      background-color: var(--switch-disabled-icon-color-off);
    }

    #on-icon,
    #off-icon {
      fill: var(--switch-disabled-icon-color-off);
    }
  }

  input:checked:disabled + &-slider {
    background-color: var(--switch-disabled-background-on);

    &:before {
      background-color: var(--switch-disabled-icon-color-on);
    }

    #on-icon,
    #off-icon {
      fill: var(--switch-disabled-icon-color-on);
    }
  }

  input:focus-visible + &-slider {
    /* using 3px outline and offset -1px to fix subpixel issues */
    outline: 3px solid $color-turquoise-03;
    outline-offset: -1px;
  }

  &-label {
    font-family: 'Bosch Sans';
    font-size: 12px;
    line-height: 12px;
    font-weight: 500;
    text-transform: uppercase;
    color: $color-grey-06;
  }
}
import { Component, h, Prop, Event, EventEmitter, AttachInternals } from '@stencil/core';

@Component({
  tag: 'dc-ui-switch',
  styleUrl: 'dc-ui-switch.scss',
  formAssociated: true,
  shadow: true,
})
export class DcUiSwitch {
  @Prop() checked: boolean = false;
  @Prop() disabled: boolean = false;
  @Prop() hideLabel: boolean = false;
  @Prop() label: string = '';
  @Prop() name: string = '';

  @Event() dcChange: EventEmitter<{ checked: boolean }>;

  @AttachInternals() internals: ElementInternals;

  private prevCheckedValue: boolean;

  componentWillLoad() {
    this.prevCheckedValue = this.checked;
  }

  componentDidUpdate() {
    if (this.checked !== this.prevCheckedValue) {
      this.dcChange.emit({ checked: this.checked });
      this.prevCheckedValue = this.checked;
    }
  }

  formAssociatedCallback() {
    this.setFormValue(this.checked);
  }

  formResetCallback() {
    this.clearSelection();
    // Reset the form value and validity state
    this.internals.setFormValue(undefined);
    this.internals.setValidity({});
  }

  private clearSelection = () => {
    this.checked = false;
  };

  private handleChange = (event: Event) => {
    const isChecked = (event.target as HTMLInputElement).checked;
    this.dcChange.emit({ checked: isChecked });
    this.setFormValue(isChecked);
  };

  private setFormValue = (checked: boolean) => {
    const value = checked ? 'on' : undefined;
    // Set form value based on the checked state
    // Here we also pass the state of the checkbox (2nd argument) as the state of the form control
    // Details: https://developer.mozilla.org/en-US/docs/Web/API/ElementInternals/setFormValue
    this.internals?.setFormValue(value, `${this.checked}`);
  };

  render() {
    const showLabel = this.label && !this.hideLabel;
    const ariaLabel = !showLabel && this.label ? this.label : null;

    return (
      <label class="dc-switch">
        {showLabel && <span class="dc-switch-label">{this.label}</span>}
        <input
          type="checkbox"
          checked={this.checked}
          disabled={this.disabled}
          onChange={this.handleChange}
          name={this.name}
          aria-label={ariaLabel}
        />
        <span class="dc-switch-slider">
          <dc-ui-icon
            color="inherit"
            id="on-icon"
            size="tiny"
            name="dc_checkmark"></dc-ui-icon>
          <dc-ui-icon
            id="off-icon"
            color="inherit"
            size="tiny"
            name="dc_close"></dc-ui-icon>
        </span>
      </label>
    );
  }
}
