import React, { useState, useEffect } from 'react';
import './ScrollToTop.css'; // Styling remains the same

const ScrollToTop = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [isStopped, setIsStopped] = useState(false);

  // Show button when page is scrolled down
  const toggleVisibility = () => {
    if (window.pageYOffset > 0) {
      setIsVisible(true);
    } else {
      setIsVisible(false);
    }

    // Calculate the button stopping point based on the window height and offset height
    const stopPosition = 100; // Adjust this value to your preference

    // Calculate if the button should stop scrolling
    if (
      window.innerHeight + window.pageYOffset >=
      document.body.offsetHeight - stopPosition
    ) {
      setIsStopped(true); // Stop the button at the specified position
    } else {
      setIsStopped(false);
    }
  };

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  };

  useEffect(() => {
    window.addEventListener('scroll', toggleVisibility);
    return () => {
      window.removeEventListener('scroll', toggleVisibility);
    };
  }, []);

  return (
    <div className={`scroll-to-top ${isStopped ? 'stopped' : ''}`}>
      {isVisible && (
        <dc-ui-button
          variant="secondary"
          size="large"
          type="button"
          hide-label="false"
          disabled="false"
          onClick={scrollToTop}
          className="scroll-to-top-button"
        >
          <dc-ui-icon name="dc_up_double" color="black" size="large" />
        </dc-ui-button>
      )}
    </div>
  );
};

export default ScrollToTop;
