import { TBreakpointNames, breakpoints } from '../../utils';

export const getCurrentBreakpoint = (): TBreakpointNames => {
  let currentBreakpoint: TBreakpointNames;
  const width = window.innerWidth;

  if (width < breakpoints.xs) {
    currentBreakpoint = 'xxs';
  } else if (width < breakpoints.sm) {
    currentBreakpoint = 'xs';
  } else if (width < breakpoints.md) {
    currentBreakpoint = 'sm';
  } else if (width < breakpoints.lg) {
    currentBreakpoint = 'md';
  } else if (width < breakpoints.xl) {
    currentBreakpoint = 'lg';
  } else if (width < breakpoints.xxl) {
    currentBreakpoint = 'xl';
  } else {
    currentBreakpoint = 'xxl';
  }

  return currentBreakpoint;
};

/**
 * Helper function to check if the current breakpoint is equal to or larger than the specified breakpoint.
 *
 * @param {TBreakpointNames} breakpoint - The breakpoint to check against.
 * @returns {boolean} - True if the current breakpoint is equal to or larger than the specified breakpoint, false otherwise.
 */
export const isBreakpointEqualOrLarger = (breakpoint: TBreakpointNames): boolean => {
  return breakpoints[getCurrentBreakpoint()] >= breakpoints[breakpoint];
};

/**
 * Helper function to check if the current breakpoint is equal to or smaller than the specified breakpoint.
 *
 * @param {TBreakpointNames} breakpoint - The breakpoint to check against.
 * @returns {boolean} - True if the current breakpoint is equal to or smaller than the specified breakpoint, false otherwise.
 */
export const isBreakpointEqualOrSmaller = (breakpoint: TBreakpointNames): boolean => {
  return breakpoints[getCurrentBreakpoint()] <= breakpoints[breakpoint];
};
