// https://github.com/facebook/react/blob/c88fb49d37fd01024e0a254a37b7810d107bdd1d/packages/react-dom/src/client/inputValueTracking.js#L53
export const observeProperties = <T extends HTMLElement>(
  node: T,
  props: (keyof T)[],
  callback: () => void
