  @Element() host!: HTMLElement;
  @Prop() width: TContentWidth = 'basic';
  @Prop() backgroundColor?: TContentBackgroundColor;
  @Prop() backgroundFull: TContentBackgroundFull;
  @Prop() slant?: TContentSlant;

  @Watch('slant')
