const getActiveSlide = (gallery: DcUiGallery) => {
  const activeSlide = gallery.el.querySelector('dc-ui-gallery-item.active');
  const index = Array.from(gallery.el.querySelectorAll('dc-ui-gallery-item')).indexOf(
    gallery.el.querySelector('dc-ui-gallery-item.active')
