  if (Array.from(gallery.slides).some((slide: HTMLElement) => slide.children.length > 1)) {
    gallery.hasThumbnails = false;
  }

  gallery.slides.forEach((slide: HTMLElement, index) => {
    slide.style.width = `${gallery.galleryWidth}px`;

    // add index to slide
    slide.setAttribute('data-slide-index', `${index}`);

    // create thumbnail element if applicable
    if (gallery.hasThumbnails && !gallery.hasRendered) {
      const li = createThumbnailElement(slide, index);
      gallery.thumbnailList.appendChild(li);
    }

    // create navigation dot
    if (!gallery.hasRendered) {
