  gallery.slides.forEach((slide) => {
    slide.style.width = `${gallery.galleryWidth}px`;
  });

  gallery.slidesWrapper.style.width = `${gallery.galleryWidth * gallery.numSlides}px`;

  const activeSlideIndex = getActiveSlide(gallery).index;
  gallery.slidesWrapper.style.transform = `translateX(-${
    gallery.galleryWidth * activeSlideIndex
  }px)`;

  // recalculate the thumbnail spacing (new viewport width might have changed it)
  setThumbnailSpacing(gallery);

  // recalculate the position of the current thumbnail page (new viewport width might have changed it)
  goToActiveThumbnailPage(gallery);

  // show navigation dots only if thumbnails aren't available
  const hasNavigationDots = !gallery.hasThumbnails || !shouldShowThumbnails();
  gallery.navigationDotList.style.display = hasNavigationDots ? 'flex' : 'none';
