/**
 * Adjust the widths of the gallery inside the modal.
 *
 * This function calculates the width of the gallery and sets the width of each slide to match it.
 * It's called when the modal is opened to ensure the gallery is displayed correctly.
 *
 * @param {DcUiGallery} gallery - The gallery object.
 *
 * @returns {void} - This function does not return anything; it directly manipulates the style
 * of the gallery and its slides.
 */
export const adjustGalleryAndSlideWidthsInModal = (gallery: DcUiGallery) => {
  const modalGalleryWidth = gallery.galleryWrapperInModal.offsetWidth;
  const modalSlides = gallery.slidesWrapperInModal.querySelectorAll('dc-ui-gallery-item');

  modalSlides.forEach((slide: HTMLElement) => {
    slide.style.width = `${modalGalleryWidth}px`;
  });

  gallery.slidesWrapperInModal.style.width = `${modalGalleryWidth * gallery.numSlides}px`;

  // recalculate the slide position in the modal (new viewport width might have changed it)
  goToSlideInModal(gallery, gallery.activeSlideIndexInModal, false);
};

/**
 * Navigates to a specific slide in the gallery.
 *
 * This function finds the index of the slide with the given ID,
 * calculates the transform needed to bring that slide into view, and applies that transform to the slides wrapper.
 * It also updates the active class on the slides and thumbnails to reflect the new active slide.
 * Finally, it calls the goToActiveThumbnailPage function to ensure the active thumbnail is in view.
 *
 * @param {DcUiGallery} gallery - The gallery object.
 * @param {string} slideIndex - The ID of the slide to navigate to.
 *
 * @returns {void} - This function does not return anything; it directly manipulates the style
 * of the gallery and its slides.
 */
export const goToSlide = (gallery: DcUiGallery, slideIndex: number) => {
  const slides = gallery.el.querySelectorAll('dc-ui-gallery-item');

  // ensure slide index is within bounds
  slideIndex = Math.max(Math.min(gallery.numSlides - 1, slideIndex), 0);

  // scroll to the specified slide
  const galleryWidth = gallery.galleryWrapper.offsetWidth;
  gallery.slidesWrapper.style.transform = `translateX(-${galleryWidth * slideIndex}px)`;

  // mark slide as active, and remove active class on all other slides
  slides.forEach((slide) => {
    slide.classList.remove('active');
  });
  slides[slideIndex].classList.add('active');

  // mark thumbnail as active, and remove active class on all other thumbnails
  if (gallery.hasThumbnails && shouldShowThumbnails()) {
    gallery.thumbnailList.querySelectorAll('li').forEach((thumb) => {
      thumb.classList.remove('active');
    });
    gallery.thumbnailList.children[slideIndex].classList.add('active');
  }

  // mark navigation dot as active and remove active class on all other navigation dots
  gallery.navigationDotList.querySelectorAll('li').forEach((dot) => {
    dot.classList.remove('active');
  });
  gallery.navigationDotList.children[slideIndex].classList.add('active');

  // mark navigation dot in modal as active and remove active class on all other navigation dots
  if (gallery.hasModal) {
    gallery.navigationDotListInModal.querySelectorAll('li').forEach((dot) => {
      dot.classList.remove('active');
    });
    gallery.navigationDotListInModal.children[slideIndex].classList.add('active');
  }

  // update active slide index
  gallery.activeSlideIndex = slideIndex;

  updateSlideNumberDisplay(gallery);
  goToActiveThumbnailPage(gallery);
};

/**
 * Moves the gallery to the next slide.
 *
 * @param {DcUiGallery} gallery - The gallery object.
 *
 * @returns {void} - This function does not return anything; it directly manipulates the style
 * of the gallery's slides wrapper.
 */
export const goToNextSlide = (gallery: DcUiGallery) => {
  const nextIndex = (gallery.activeSlideIndex + 1) % gallery.numSlides;
  goToSlide(gallery, nextIndex);
};

/**
 * Moves the gallery to the previous slide.
 *
 * @param {DcUiGallery} gallery - The gallery object.
 *
 * @returns {void} - This function does not return anything; it directly manipulates the style
 * of the gallery's slides wrapper.
 */
export const goToPrevSlide = (gallery: DcUiGallery) => {
  // add numSlides to ensure the number is positive after mod operation
  const prevIndex = (gallery.numSlides + gallery.activeSlideIndex - 1) % gallery.numSlides;
  goToSlide(gallery, prevIndex);
};

/**
 * Navigates to a specific slide in the gallery inside the modal.
 *
 * This function finds the index of the slide with the given ID,
 * calculates the transform needed to bring that slide into view,
 * and applies that transform to the slides wrapper.
 * It also updates the active class on the slides to reflect the new active slide.
 *
 * @param {DcUiGallery} gallery - The gallery object.
 * @param {string} slideIndex - The ID of the slide to navigate to.
 * @param {boolean} animate - Whether to enable the smooth slide change animation.
 *
 * @returns {void} - This function does not return anything; it directly manipulates the style
 * of the gallery and its slides.
 */
export const goToSlideInModal = (
  gallery: DcUiGallery,
  slideIndex: number,
  animate: boolean = true
) => {
  if (!gallery.hasModal) {
    return;
  }

  // ensure slide index is within bounds
  slideIndex = Math.max(Math.min(gallery.numSlides, slideIndex), 0);

  if (!animate) {
    // temporarily remove transition to prevent animation
    gallery.slidesWrapperInModal.classList.add('no-transition');
    setTimeout(() => {
      gallery.slidesWrapperInModal.classList.remove('no-transition');
    }, 0);
  }

  const slides = gallery.slidesWrapperInModal.querySelectorAll('dc-ui-gallery-item');
  const galleryWidth = gallery.galleryWrapperInModal.offsetWidth;
  gallery.slidesWrapperInModal.style.transform = `translateX(-${galleryWidth * slideIndex}px)`;

  slides.forEach((slide) => slide.classList.remove('active'));
  slides[slideIndex].classList.add('active');

  // update active slide index
  gallery.activeSlideIndexInModal = slideIndex;

  // mark navigation dot in modal as active and remove active class on all other navigation dots
  gallery.navigationDotListInModal.querySelectorAll('li').forEach((dot) => {
    dot.classList.remove('active');
  });
  gallery.navigationDotListInModal.children[slideIndex].classList.add('active');
};

/**
 * Navigates to the next slide in the modal.
 *
 * @param {DcUiGallery} gallery - The gallery object.
 *
 * @returns {void} - This function does not return anything; it directly manipulates the style
 * of the gallery's slides wrapper.
 */
export const goToNextSlideInModal = (gallery: DcUiGallery) => {
  if (!gallery.hasModal) {
    return;
  }

  const activeSlide = gallery.slidesWrapperInModal.querySelector('dc-ui-gallery-item.active');
  const activeSlideIndex = Array.from(gallery.slidesWrapperInModal.children).indexOf(activeSlide);

  const nextIndex = (activeSlideIndex + 1) % gallery.numSlides;

  goToSlideInModal(gallery, nextIndex);
};

/**
 * Navigates to the previous slide in the modal.
 *
 * @param {DcUiGallery} gallery - The gallery object.
 *
 * @returns {void} - This function does not return anything; it directly manipulates the style
 * of the gallery's slides wrapper.
 */
export const goToPrevSlideInModal = (gallery: DcUiGallery) => {
  if (!gallery.hasModal) {
    return;
  }

  const activeSlide = gallery.slidesWrapperInModal.querySelector('dc-ui-gallery-item.active');
  const activeSlideIndex = Array.from(gallery.slidesWrapperInModal.children).indexOf(activeSlide);

  // add numSlides to ensure the number is positive after mod operation
  const prevIndex = (gallery.numSlides + activeSlideIndex - 1) % gallery.numSlides;

  goToSlideInModal(gallery, prevIndex);
};

/**
 * Navigates to the specified page of thumbnails.
 *
 * This function scrolls (transforms) the gallery to the specified page.
 * The calculation considers the width of the thumbnail list, the number of thumbnails per view,
 * and the calculated gap between thumbnails.
 *
 * @param {DcUiGallery} gallery - The gallery object.
 * @param {number} page - The page number.
 *
 * @returns {void} - This function does not return anything; it directly manipulates the style
 * of the gallery's thumbnail list.
 */
const goToThumbnailPage = (gallery: DcUiGallery, page: number) => {
  if (!gallery.hasThumbnails || !shouldShowThumbnails()) {
    return;
  }

  // ensure page is within bounds
  page = Math.max(Math.min(gallery.numThumbnailPages - 1, page), 0);

  gallery.currentThumbnailPage = page;
  const gapOffset = page * gallery.thumbnailCalculatedGap;
  const xOffset = page * gallery.thumbnailList.offsetWidth + gapOffset;

  gallery.thumbnailList.style.transform = `translateX(-${xOffset}px)`;
};

/**
 * Navigates to the next page of thumbnails.
 *
 * @param {DcUiGallery} gallery - The gallery object.
 *
 * @returns {void} - This function does not return anything; it directly manipulates the style
 * of the gallery's thumbnail list.
 */
export const goToNextThumbnailPage = (gallery: DcUiGallery) => {
  if (!gallery.hasThumbnails || !shouldShowThumbnails()) {
    return;
  }

  const nextPage = gallery.currentThumbnailPage + 1;
  goToThumbnailPage(gallery, nextPage % gallery.numThumbnailPages);
};

/**
 * Navigates to the previous page of thumbnails.
 *
 * @param {DcUiGallery} gallery - The gallery object.
 *
 * @returns {void} - This function does not return anything; it directly manipulates the style
 * of the gallery's thumbnail list.
 */
export const goToPrevThumbnailPage = (gallery: DcUiGallery) => {
  if (!gallery.hasThumbnails || !shouldShowThumbnails()) {
    return;
  }

  // add numThumbnailPages to ensure the number is positive after mod operation
  const prevPage = gallery.numThumbnailPages + gallery.currentThumbnailPage - 1;
  goToThumbnailPage(gallery, prevPage % gallery.numThumbnailPages);
};

/**
 * Navigates to the thumbnail page that contains the active thumbnail.
 *
 * @param {DcUiGallery} gallery - The gallery object.
 *
 * @returns {void} - This function does not return anything; it directly manipulates the style
 * of the gallery's thumbnail list.
 */
const goToActiveThumbnailPage = (gallery: DcUiGallery) => {
  if (!gallery.hasThumbnails || !shouldShowThumbnails()) {
    return;
  }

  const activeThumbnailPage = Math.floor(
    getActiveThumbnail(gallery).index / gallery.numThumbnailsPerPage
  );
  goToThumbnailPage(gallery, activeThumbnailPage);
};

/**
 * Determines if thumbnails should be shown (they should be hidden on mobile).
 *
 * @returns {boolean}
 */
const shouldShowThumbnails = () => {
  return isBreakpointEqualOrLarger(THUMBNAIL_SHOWN_BREAKPOINT);
};
$dc-ui-image-gallery-transition-time: 300ms;

@media screen and (prefers-reduced-motion: reduce) {
  // todo test if this works
  $dc-ui-image-gallery-transition-time: 0s;
}

:host {
  --_thumbnail-width: 70px;
  --_aspect-ratio: initial;
  --_aspect-ratio-padding-top: initial;
}

.dc-ui-image-gallery {
  box-sizing: border-box;
  display: flex;
  flex-direction: column;

  * {
    box-sizing: border-box;
  }

  &--ratio-16-9 {
    --_aspect-ratio: 16 / 9;
    --_aspect-ratio-padding-top: 28.125%;
  }

  &--ratio-1-1 {
    --_aspect-ratio: 1 / 1;
    --_aspect-ratio-padding-top: 50%;
  }

  .gallery-wrapper {
    position: relative;
    width: 100%;
    overflow: hidden;

    .gallery-button-container {
      position: absolute;
      top: 0;
      padding-top: var(--_aspect-ratio-padding-top);
      z-index: 1;

      &--left {
        left: 0;
      }

      &--right {
        right: 0;
      }
    }

    .gallery-button {
      opacity: 0;
      transform: translateY(-50%);
      pointer-events: none;
    }

    @include media-min('md') {
      &:hover,
      &:focus-within {
        .gallery-button {
          opacity: 1;
          pointer-events: all;
        }
      }
    }

    // if hover is available, show buttons regardless of viewport size
    // (that way, in case swiping somehow doesn't work [e.g. small desktop windows], the buttons are still there)
    @media (hover: hover) {
      &:hover,
      &:focus-within {
        .gallery-button {
          opacity: 1;
          pointer-events: all;
        }
      }
    }

    &::after {
      content: '';
      display: block;
      position: absolute;
      inset: 0 0 auto;
      border: 1px solid $color-dark-blue-01;
      aspect-ratio: var(--_aspect-ratio);
      pointer-events: none;
    }
  }

  dc-ui-modal {
    --_aspect-ratio-padding-top: 28.125%; // fixed 16:9 aspect ratio for modal

    .navigation-dots {
      margin-top: 24px;
    }
  }

  .slides,
  .modal-slides {
    touch-action: pan-y;
    display: flex;
    height: 100%;
    transition: transform $dc-ui-image-gallery-transition-time ease;

    &.no-transition {
      transition: none;
    }
  }

  dc-ui-gallery-item {
    flex-shrink: 0;

    img {
      width: 100%;
      height: 100%;
      max-width: 100%;
      max-height: 100%;
      aspect-ratio: var(--_aspect-ratio);
      object-fit: contain;
      object-position: top;
    }
  }
}

.thumbnails {
  overflow-x: clip;
  display: none;
  flex-direction: column;
  gap: 16px;

  @include media-min('md') {
    display: flex;
  }

  &__list {
    list-style: none;
    padding: 0;
    margin: 16px 0 0;
    display: flex;
    transition: transform $dc-ui-image-gallery-transition-time ease;

    &.no-transition {
      transition: none;
    }

    li {
      display: flex;
      border: 1px solid $color-dark-blue-01;
      height: var(--_thumbnail-width);
      width: var(--_thumbnail-width);
      min-width: var(--_thumbnail-width);

      &.active {
        border-color: $color-dark-blue-02;
      }

      &:has(:focus-visible) {
        border-color: $color-turquoise-03;
      }

      dc-ui-gallery-item {
        align-items: center;
        justify-content: center;
      }
    }

    button.thumbnail {
      border: none;
      background: none;
      cursor: pointer;
      padding: 0;
      margin: 0;
      display: block;
      width: 100%;
      height: 100%;

      &:focus-visible {
        outline: 1px solid $color-turquoise-03;
        outline-offset: -1px;
      }

      img,
      picture {
        width: 100%;
        height: 100%;
        max-width: 100%;
        max-height: 100%;
        object-fit: contain;
      }
    }
  }

  &__info {
    display: flex;
    justify-content: space-between;
    align-items: center;

    &__cta {
      display: flex;
      gap: 8px;
    }
  }
}

.navigation-dots {
  list-style-type: none;
  margin: 16px 0 0;
  padding: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;

  li {
    position: relative;
    display: grid;
    place-items: center;
    width: 24px;
    padding: 0;
    aspect-ratio: 1;
    line-height: 0;

    button.dot {
      width: 100%;
      height: 100%;
      margin: 0;
      border: none;
      background: none;
      padding: 0;
      cursor: pointer;
    }

    &::after {
      content: '';
      position: absolute;
      width: 8px;
      aspect-ratio: 1;
      border-radius: 100%;
      background-color: $color-grey-05;
      pointer-events: none;
      transition: all $dc-ui-image-gallery-transition-time;
      opacity: 1;
    }

    &.active {
      width: 28px;

      &::after {
        width: 12px;
        background-color: $color-dark-blue-02;
      }
    }
  }

  &.truncated {
    $num-dots: 3;

    li {
      transition: all $dc-ui-image-gallery-transition-time;
    }

    li:nth-child(#{$num-dots - 1} of .active ~ li),
    li:nth-last-child(#{$num-dots - 1} of :has(~ .active)) {
      &::after {
        opacity: 0.5;
      }
    }

    li:nth-child(#{$num-dots} of .active ~ li),
    li:nth-last-child(#{$num-dots} of :has(~ .active)) {
      &::after {
        opacity: 0.25;
      }
    }

    li:nth-child(#{'n + #{$num-dots + 1} of .active ~ li'}),
    li:nth-last-child(#{'n + #{$num-dots + 1} of :has(~ .active)'}) {
      width: 0;

      &::after {
        opacity: 0;
      }
    }
  }
}
import { Component, Element, Method, Prop, h } from '@stencil/core';
import { isBreakpointEqualOrLarger } from '../../utils';
import {
  TGalleryRatio,
  adjustGalleryAndSlideWidths,
  adjustGalleryAndSlideWidthsInModal,
