import { Component, h, Prop, Element } from '@stencil/core';
import { TGalleryRatio } from '../dc-ui-gallery/gallery-utils';

@Component({
  tag: 'dc-ui-gallery-item',
  styleUrl: 'dc-ui-gallery-item.scss',
  shadow: true,
})
export class DcUiGalleryItem {
  @Element() el: HTMLElement;
  @Prop() ratio: TGalleryRatio;

  private copyContainer: HTMLElement;

  GALLERY_RATIOS_CSS = {
    '16:9': 'dc-ui-gallery-item--ratio-16-9',
    '1:1': 'dc-ui-gallery-item--ratio-1-1',
  };

  componentDidRender() {
    //** check if slot headline or slot text is empty */
    const headline = this.el.querySelector('[slot="headline"]');
    const text = this.el.querySelector('[slot="text"]');
    if (!headline && !text) {
      // remove copy div if no headline or text
      this.copyContainer.style.display = 'none';
    }
  }

  returnCSSClasses = () => {
    const classes = {
      'dc-ui-gallery-item': true,
    };
    if (this.GALLERY_RATIOS_CSS[this.ratio]) {
      classes[this.GALLERY_RATIOS_CSS[this.ratio]] = true;
    }
    return classes;
  };

  render() {
    // todo disallow slides without images (destroy itself when no image?)

    return (
      <div class={this.returnCSSClasses()}>
        <div class="image">
          <slot></slot>
        </div>
        <div
          class="copy"
          ref={(el) => (this.copyContainer = el)}>
          <slot name="headline"></slot>
          <slot name="text"></slot>
        </div>
      </div>
    );
  }
}
