export const returnTypeStyles = (
  type: string,
  labelPosition: TInputLabelPosition,
  labelWidth: BreakpointCustomizable<string>,
  hideLabel: BreakpointCustomizable<boolean>,
  labelColor: string
): Styles => {
  switch (type) {
    case 'text':
      return {
        ...getLabelStyles(type, labelPosition, hideLabel, labelColor, labelWidth),
      };

    case 'search':
      return {
        ...getLabelStyles(type, labelPosition, hideLabel, labelColor, labelWidth),
      };

    case 'date':
      return {
        ...getLabelStyles(type, labelPosition, hideLabel, labelColor, labelWidth),
      };

    case 'email':
      return {
        ...getLabelStyles(type, labelPosition, hideLabel, labelColor, labelWidth),
      };

    case 'number':
      return {
        ...getLabelStyles(type, labelPosition, hideLabel, labelColor, labelWidth),
      };

    case 'password':
      return {
        ...getLabelStyles(type, labelPosition, hideLabel, labelColor, labelWidth),
      };

    case 'tel':
      return {
        ...getLabelStyles(type, labelPosition, hideLabel, labelColor, labelWidth),
      };

    case 'url':
      return {
        ...getLabelStyles(type, labelPosition, hideLabel, labelColor, labelWidth),
      };

    case 'file':
      return {
        ...getFileInputStyles(),
      };
  }
