export const createStyles = (
  labelPosition: TInputLabelPosition,
  labelWidth: BreakpointCustomizable<string>,
  hideLabel: BreakpointCustomizable<boolean>,
  disabled: boolean,
  type: string,
  state: TValidationState,
  readonly: boolean
): string => {
  const { baseLabelColor, baseInputColor, baseBorderColor } = getTextInputColors(
    state,
    disabled,
    readonly
  );
  const stateColor = disabled ? colorUtils.grey02 : getStateColors(state);
  const stateToPass = disabled ? 'none' : state;

  return getCss({
    '@global': {
      ':host': {
        display: 'block',
        fontFamily: 'Bosch Sans',
      },
      ...returnTypeStyles(type, labelPosition, labelWidth, hideLabel, baseLabelColor),
      '::slotted': addImportantToEachRule({
        '&(input)': {
          all: 'unset',
          appearance: 'none',
          WebkitAppearance: 'none', // iOS safari
          fontFamily: 'Bosch Sans',
          fontSize: '14px',
          lineHeight: '16px',
          letterSpacing: '0.16px',
          color: baseInputColor,
          outline: `2px solid transparent`,
          outlineOffset: '-2px',
          border: 'none',
          resize: 'none',
          backgroundColor: colorUtils.white,
          ...getBorderStateStyles(stateToPass, baseBorderColor),
          ...getInputPaddingStyles(type, labelPosition, stateToPass),
        },

        '&(input:disabled)': {
          cursor: 'not-allowed',
          backgroundColor: colorUtils.white,
        },

        '&(input:hover)': {
          borderColor: stateColor || colorUtils.grey10,
        },

        '&(input:focus)': {
          outlineColor: stateColor || colorUtils.focusColor,
        },

        // needed for Safari
        '&(input:focus:not(:focus-visible))': {
          outlineColor: 'transparent',
        },

        '&(input[type="file"])': {
          display: 'none',
        },
      }),
      'dc-ui-icon#error-icon': {
        position: 'absolute',
        ...iconPosition(labelPosition, type),
      },
      '.file-container': {
        display: 'none',
        placeContent: 'center',
        border: `1px solid ${colorUtils.grey03}`,
        padding: '10px 20px',

        '&.file-selected': {
          display: 'grid',
        },
      },
      '#search-button, #clear-search-button': {
        position: 'absolute',
        appearance: 'none',
        border: 'none',
        top: '0',
        bottom: '0',
        backgroundColor: 'transparent',
        ...getSearchButtonStyles(labelPosition),

        '&:focus-visible': {
          outline: `2px solid ${stateColor || colorUtils.focusColor}`,
          outlineOffset: '-2px',
        },
      },
      '#clear-search-button': {
        right: '0',
      },
      '#search-icon': {
        position: 'absolute',
        border: 'none',
