        position: 'relative',
      },
    },
    ...getStateMessageStyles(state),
  });
};

export const getSlottedCss = (host: HTMLElement, labelPosition: TInputLabelPosition): string => {
  return getCss(
    buildSlottedStyles(host, {
      // the following selectors don't work within ::slotted() pseudo selector, therefore we have to apply them via light DOM
      '& input::-webkit-outer-spin-button, & input::-webkit-inner-spin-button, & input[type="search"]::-webkit-search-decoration':
        {
          WebkitAppearance: 'none',
          appearance: 'none',
        },
      '& input[type="search"]::-webkit-search-cancel-button': {
        display: 'none',
      },
      '& input[type="date"]::-webkit-calendar-picker-indicator': {
        opacity: '0',
        position: 'absolute',
        right: '16px',
        ...getDateIconStyles(labelPosition),
      },
      '& input[type="text"]': {
        '&::-webkit-contacts-auto-fill-button, &::-webkit-credentials-auto-fill-button': {
          marginRight: '2.4375rem',
        },
      },
    })
  );
};
import { ButtonType } from '../types/button';
import { getClosestHTMLElement } from './getClosestHtmlElement';

export const handleButtonEvent = (
  event: MouseEvent,
  element: HTMLElement,
  getType: () => ButtonType,
  getDisabled: () => boolean
): void => {
  // Why? That's why: https://www.hjorthhansen.dev/shadow-dom-and-forms/
  const form = getClosestHTMLElement(element, 'form');
  if (form && !getDisabled()) {
    /**
     * we've to wait if someone calls preventDefault on the event
     * then we shouldn't submit the form
     */
    window.setTimeout(() => {
      if (!event.defaultPrevented) {
        const fakeButton = document.createElement('button');
        fakeButton.type = getType();
        fakeButton.style.display = 'none';
        form.appendChild(fakeButton);
        fakeButton.addEventListener('click', (fakeButtonEvent) => {
          fakeButtonEvent.stopPropagation();
        });
        fakeButton.click();
        fakeButton.remove();
      }
    }, 1);
  }
};
import {
  Component,
  h,
  Prop,
  Host,
  Element,
  forceUpdate,
  State,
  Event,
  EventEmitter,
} from '@stencil/core';
import {
  BreakpointCustomizable,
  injectCSS,
  setAriaAttributes,
  observeAttributes,
  injectFont,
  observeProperties,
  attachSlottedCss,
  createUuid,
  TValidationState,
} from '../../utils';
import {
  addInputEventListenerForSearch,
  isInsideForm,
  TInputClearEvent,
  TInputLabelPosition,
} from './input-utils';
import { createStyles, getSlottedCss } from './input-styles';
import { handleButtonEvent } from '../../utils/functions/buttonHandling';
import { StateMessage } from '../common/state-message/state-message';

@Component({
  tag: 'dc-ui-input-wrapper',
  shadow: true,
})
export class DcUiInputWrapper {
  @Element() el: HTMLElement;
  @Prop() label: string;
  @Prop() labelPosition: TInputLabelPosition = 'default';
  @Prop() labelWidth?: BreakpointCustomizable<string> = '25%';
  @Prop() message?: string = '';
  @Prop() validationstate?: TValidationState = 'none';
  @Prop() hideLabel: BreakpointCustomizable<boolean>;
  @Prop() filedrop?: boolean;

  @State() private isClearable = false;

  // Fire event when input is cleared
  @Event({ bubbles: false }) clear: EventEmitter<TInputClearEvent>;

  private isFile: boolean;
  private isSearch: boolean;
  private isDate: boolean;
  private isInsideForm: boolean;

  private input: HTMLInputElement;
  labelEl: HTMLLabelElement;

  public componentWillLoad(): void {
    injectFont();

    const fordbiddenTypes = [
      'button',
      'checkbox',
      'color',
      'datetime-local',
      'image',
      'month',
      'radio',
      'range',
      'reset',
      'submit',
      'time',
      'week',
    ];

    // filter the input by type from array of type strings
    if (!fordbiddenTypes.includes(this.el.querySelector('input').type)) {
      this.input = this.el.querySelector('input');
    }

    this.isFile = this.input.type === 'file';
    this.isSearch = this.input.type === 'search';
    this.isInsideForm = isInsideForm(this.el);
    this.isDate = this.input.type === 'date';
    // this.isPassword = this.input.type === 'password';

    if (this.isSearch) {
      this.isClearable = !!this.input.value;
      observeProperties(this.input, ['value'], () => (this.isClearable = !!this.input.value));
    }

    this.observeAttributes(); // once initially
  }

  public componentDidLoad(): void {
    if (this.isSearch) {
      addInputEventListenerForSearch(this.input, (hasValue) => (this.isClearable = hasValue));
    }
  }

  public connectedCallback(): void {
    attachSlottedCss(this.el, getSlottedCss(this.el, this.labelPosition));
    this.observeAttributes(); // on every reconnect
  }

  public componentDidRender(): void {
    setAriaAttributes(this.input, {
      label: this.label,
      message: this.message,
    });

    // check if textarea is not empty or has a placeholder and add focus class to label
    if (this.input.value !== '' || this.input.placeholder !== '') {
      this.labelEl.classList.add('focus');
    }

    // event on input focus
    if (this.isFile) {
      this.input.addEventListener('change', () => {
        const fileName = this.input.value.split('\\').pop();
        const fileContainer = this.el.shadowRoot.querySelector('.file-container');
        const fileNameEl = this.el.shadowRoot.querySelector('#file-name');

        if (fileName) {
          fileContainer.classList.add('file-selected');
          fileNameEl.textContent = fileName;
        } else {
          fileContainer.classList.remove('file-selected');
          fileNameEl.textContent = '';
        }
      });

      return;
    }

    this.input.addEventListener('focus', () => {
      this.labelEl.classList.add('focus');
    });

    // event on input blur if not datepicker
    if (!this.isDate) {
      this.input.addEventListener('blur', () => {
        // remove focus class from label if input is empty
        if (this.input.value === '' && this.input.placeholder === '')
          this.labelEl.classList.remove('focus');
      });
    }
  }

  private observeAttributes = (): void => {
    observeAttributes(this.input, ['disabled', 'readonly', 'required'], () => forceUpdate(this.el));
  };

  private onLabelClick = (): void => {
    this.input.focus();
  };

  private onFileButtonClick = (): void => {
    this.input.click();
  };

  private onClear = (): void => {
    this.input.focus();
    this.input.value = '';
    this.input.removeAttribute('value');
    this.clear.emit({ clear: true });
  };

  private onSubmit = (event: MouseEvent): void => {
    handleButtonEvent(
      event,
      this.el,
      () => 'submit',
      () => this.input.disabled
    );
  };

  private getInputId = (): string => {
    if (this.input.id) {
      return this.input.id;
    } else {
      // generate random id and set it to input
      const id = createUuid();
      this.input.id = id;
      return id;
    }
  };

  render() {
    injectCSS(
      this.el,
      createStyles,
      this.labelPosition,
      this.labelWidth,
      this.hideLabel,
      this.input.disabled,
      this.input.type,
      this.validationstate,
      this.input.readOnly
    );

    return (
      <Host>
        {this.isFile && (
          <label>
            <dc-ui-button
              variant={this.validationstate === 'error' ? 'primary' : 'light'}
              disabled={this.input.disabled}
              onClick={this.onFileButtonClick}>
              {this.label} {this.input.required && ' *'}
            </dc-ui-button>
            <div class="file-container">
              <span id="file-name"></span>
            </div>
            <slot />
          </label>
        )}

        {!this.isFile && (
          <label
            class={{
              focus: this.isDate,
            }}
            ref={(label) => (this.labelEl = label)}
            htmlFor={this.getInputId()}>
            <div id="label-container">
              {this.label && (
                <span
                  id="label-text"
                  onClick={this.onLabelClick}>
                  {this.label} {this.input.required ? ' *' : ''}
                </span>
              )}
            </div>
            <div id="input-container">
              {this.isSearch && [
                this.isInsideForm ? (
                  <button
                    id="search-button"
                    onClick={this.onSubmit}
                    type="submit">
                    <dc-ui-icon
                      color="darkBlue02"
                      size="tiny"
                      name="dc_search"></dc-ui-icon>
                  </button>
                ) : (
                  <dc-ui-icon
                    id="search-icon"
                    color="darkBlue02"
                    size="tiny"
                    name="dc_search"></dc-ui-icon>
                ),
              ]}
              <slot />
              {this.isSearch && this.isClearable && (
                <button
                  onClick={this.onClear}
                  id="clear-search-button"
                  hidden={!this.isClearable}>
                  <dc-ui-icon
                    color="darkBlue02"
                    size="tiny"
                    name="dc_close"></dc-ui-icon>
                </button>
              )}
              {this.isDate && (
                <dc-ui-icon
                  id="calendar-icon"
                  name="dc_calendar"
                  color="darkBlue02"
                  size="tiny"></dc-ui-icon>
              )}
              {this.validationstate !== 'none' && (
                <StateMessage
                  state={this.validationstate}
                  message={this.message}></StateMessage>
              )}
            </div>
          </label>
        )}
      </Host>
    );
  }
}
import { DcUiModal } from './dc-ui-modal';

export const MODAL_BACKDROP_COLOR = ['none', 'dark'] as const;
export type TModalBackdropColor = (typeof MODAL_BACKDROP_COLOR)[number];

export const MODAL_MAX_WIDTH = ['small', 'medium', 'large'] as const;
export type TModalMaxWidth = (typeof MODAL_MAX_WIDTH)[number];

/**
 * Adds event listeners to toggle the tooltip.
 *
 * @param tooltip
 */
export const addTriggerListeners = (modal: DcUiModal): void => {
  modal.triggerEl.addEventListener('click', modal.onTriggerClick);
};
* {
  box-sizing: border-box;
}

.dc-ui-modal {
  border: 1px solid rgba($color-black, 0.2);
  background-color: $color-white;
  box-shadow: 0 0 16px 0 rgba($color-black, 0.25);
  padding: 0;
  max-height: calc(100dvh - 32px);
  width: auto;

  &[open] {
    animation: modalFadeIn 0.25s ease-in-out;
  }

  &::backdrop {
    background-color: transparent;
  }

  @include media-max(lg) {
    max-width: calc(100dvw - 32px);
    max-height: auto;
    height: fit-content;
  }

  .dialog-body {
    min-height: 100%;
    padding: 0 24px;
  }

  .dialog-content {
    display: flex;
    flex-direction: column;

    & > :last-child {
      padding-bottom: 24px;
    }
  }

  .dialog-header {
    display: grid;
    grid-template-columns: 1fr auto;
    min-height: 48px;
    padding-block: 16px;
    background-color: $color-white;
    top: auto;
    position: static;

    @include media-max(lg) {
      position: static;
      top: auto;
    }
  }

  #close-dialog {
    grid-column: 2 / 3;
  }

  .dialog-footer {
    padding-block: 16px;
    bottom: auto;
    position: static;
    background-color: $color-white;
  }

  // sizes
  &--small {
    max-width: 640px;
  }

  /* medium is the default size */
  &--medium {
    max-width: 1024px;
  }

  &--large {
    max-width: 1440px;
  }

  // variant styles
  &--backdrop-dark {
    &::backdrop {
      background-color: rgba($color-dark-blue-02, 0.7);
    }
  }

  &--mobile-fullscreen {
    @include media-max(lg) {
      max-width: 100dvw;
      max-height: unset;
      height: 100dvh;
    }
  }

  &--sticky-header {
    .dialog-header {
      top: 0;
      position: sticky;

      @include media-max(lg) {
        position: sticky;
        top: 0;
      }
    }
  }

  &--sticky-footer {
    .dialog-footer {
      bottom: 0;
      position: sticky;
    }
  }
}

@keyframes modalFadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
import { Component, h, Element, Method, Prop, Watch, EventEmitter, Event } from '@stencil/core';
import { setScrollLock, observeAttributes } from '../../utils';
import { addTriggerListeners, TModalBackdropColor, TModalMaxWidth } from './modal-utils';
// import { createStyles } from './modal-styles';

@Component({
  tag: 'dc-ui-modal',
  styleUrl: 'dc-ui-modal.scss',
  shadow: true,
})
export class DcUiModal {
  @Element() el: HTMLDcUiModalElement;
  /** Sets the color of the modal backdrop */
  @Prop() backdropColor: TModalBackdropColor = 'none';
  /** Toggle the visibility of the modal */
  @Prop({ mutable: true, reflect: true }) isOpen = false;
  /** Modal is fullscreen on mobile */
  @Prop() mobileFullscreen = false;
  /** Show/hide the close icon inside the modal */
  @Prop() closeIcon = true;
  /** Disables the backdrop click to close the modal */
  @Prop() noBackdropClick = false;
  /** Max width of the modal */
  @Prop() maxWidth: TModalMaxWidth = 'medium';
  /** Title inside the modal header */
  @Prop() modalTitle = '';
  /** Enables the sticky header */
  @Prop() stickyHeader = false;
  /** Enables the sticky footer */
  @Prop() stickyFooter = false;
  /** (Optional) id of the trigger element. If the id is set, the modal will be opened on click of the trigger element */
  @Prop() triggerId = '';
  /**
   * @deprecated is not needed. dialog is in toplevel-layer
   * (Optional) adds different z-index to the modal */
  @Prop() zIndex?: number;

  triggerEl: HTMLElement;
  hasHeaderSlot: boolean;
  hasFooterSlot: boolean;
  private modal: HTMLDialogElement;

  /** Handler to be called when the accordion is opened */
  @Event() dcOpen: EventEmitter<HTMLDcUiModalElement>;

  /** Handler to be called when the accordion is closed */
  @Event() dcClose: EventEmitter<HTMLDcUiModalElement>;

  @Watch('isOpen')
  onIsOpenChange(isOpen: boolean): void {
    setScrollLock(isOpen);
    if (isOpen) {
      this.dcOpen.emit(this.el);
    } else {
      this.dcClose.emit(this.el);
    }
  }

  connectedCallback() {
    // get reference of the root element
    const shadowRoot = this.el.getRootNode() as ShadowRoot;

    // search for trigger element. Either in light DOM or in shadow DOM
    this.triggerEl = shadowRoot.getElementById(this.triggerId);
    this.observeAttributes(); // on every reconnect
  }

  componentWillLoad() {
    // check if there is a header or footer slot
    this.hasHeaderSlot = !!this.el.querySelector('[slot="header"]');
    this.hasFooterSlot = !!this.el.querySelector('[slot="footer"]');
  }

  componentDidRender() {
    this.modal = this.el.shadowRoot.querySelector('dialog');

    if (this.isOpen) {
      setScrollLock(true);
      this.modal.showModal();
    }

    this.observeAttributes(); // once initially

    if (this.triggerEl) {
      addTriggerListeners(this);
    }
  }

  disconnectedCallback() {
    setScrollLock(false);
  }

  // method to open the modal
  @Method() open(): Promise<boolean> {
    return new Promise((resolve) => {
      this.modal.showModal();
      resolve(true);
    });
  }

  // method to close the modal
  @Method() close(): Promise<boolean> {
    return new Promise((resolve) => {
      this.modal.close();
      resolve(true);
    });
  }

  onTriggerClick = () => {
    this.modal.showModal();
  };

  private observeAttributes = (): void => {
    observeAttributes(this.modal, ['open'], () => (this.isOpen = this.modal.hasAttribute('open')));
  };

  // close modal on backdrop click if noBackdropClick is false
  private onBackdropClick = (e: MouseEvent) => {
    if (!this.noBackdropClick && e.target === this.modal) {
      this.close();
    }
  };

  returnCSSClasses = () => {
    return {
      'dc-ui-modal': true,
      [`dc-ui-modal--${this.maxWidth}`]: true,
      'dc-ui-modal--sticky-header': this.stickyHeader,
      'dc-ui-modal--sticky-footer': this.stickyFooter,
      'dc-ui-modal--mobile-fullscreen': this.mobileFullscreen,
      'dc-ui-modal--backdrop-dark': this.backdropColor === 'dark',
    };
  };

  render() {
    return (
      <dialog
        onClick={this.onBackdropClick}
        class={this.returnCSSClasses()}>
        <div class="dialog-body">
          <div class="dialog-content">
            <div class="dialog-header">
              {this.modalTitle && !this.hasHeaderSlot && (
                <dc-ui-headline
                  no-margin="true"
                  tag="h2"
                  variant="h4"
                  color="darkBlue02">
                  {this.modalTitle}
                </dc-ui-headline>
              )}
              {this.closeIcon && (
                <dc-ui-button
                  id="close-dialog"
                  icon="dc_close"
                  onClick={() => this.close()}
                  variant="link"
                  size="small"
                  hide-label="true">
                  Close modal
                </dc-ui-button>
              )}
              <slot name="header" />
            </div>
            <div class="dialog-main">
              <slot />
            </div>
            {this.hasFooterSlot && (
              <div class="dialog-footer">
                <slot name="footer" />
              </div>
            )}
          </div>
        </div>
      </dialog>
    );
  }
}
import { getCss, buildResponsiveStyles } from '../../utils';
import { TSpacerDirection, SpacerDirectionType, TSpacerSize, SpacerSizeType } from './spacer-utils';

export const createStyles = (direction: TSpacerDirection, size: TSpacerSize): string => {
  return getCss({
    '@global': {
      ':host': buildResponsiveStyles(direction, (directionResponsive: SpacerDirectionType) => ({
        display: directionResponsive === 'horizontal' ? 'block' : 'inline-block',
        ...buildResponsiveStyles(size, (sizeResponsive: SpacerSizeType) => ({
          height: directionResponsive === 'horizontal' ? `${sizeResponsive}px` : 'auto',
          width: directionResponsive === 'horizontal' ? '100%' : `${sizeResponsive}px`,
        })),
      })),
    },
  });
};
import { Component, h, Prop, Element, Host } from '@stencil/core';
import { injectCSS } from '../../utils';
import { createStyles } from './spacer-styles';
import { TSpacerDirection, TSpacerSize } from './spacer-utils';

