import {
  getCss,
  buildResponsiveStyles,
  buildResponsiveTextStyles,
  mergeDeep,
  color as colorUtils,
} from '../../utils';
import {
  TTextTag,
  TextAlignType,
  TTextAlign,
  TTextVariant,
  TextColorType,
  TTextColor,
  TTextWeight,
} from './text-utils';

export const createStyles = (
  tag: TTextTag,
  variant: TTextVariant,
  color: TTextColor,
  align: TTextAlign,
  weight: TTextWeight
): string => {
  const styles = getCss({
    '@global': {
      ':host': {
        // if default tag is displayed as block use display: block
        display: 'block',
      },
      [tag as string]: {
        display: 'inline-block',
        fontFamily: 'Bosch Sans',
        textDecoration: 'inherit',
        margin: '0',
        ...mergeDeep(
          buildResponsiveTextStyles(variant as string),
          buildResponsiveStyles(color, (colorResponsive: TextColorType) => ({
            color: colorResponsive === 'inherit' ? 'inherit' : colorUtils[colorResponsive],
          })),
          buildResponsiveStyles(align, (alignResponsive: TextAlignType) => ({
            textAlign: alignResponsive,
          })),
          weight
            ? buildResponsiveStyles(weight, (weightResponsive: TTextWeight) => ({
                fontWeight: weightResponsive,
              }))
            : {}
        ),
      },
    },
  });

  return styles;
};
import { Component, h, Prop, Element } from '@stencil/core';
import { injectFont, injectCSS } from '../../utils';
import { TTextTag, TTextAlign, TTextWeight, TTextVariant, TTextColor } from './text-utils';
import { createStyles } from './text-styles';

@Component({
  tag: 'dc-ui-text',
  shadow: true,
})
export class DcUiText {
  @Element() el: HTMLElement;
  @Prop() tag: TTextTag = 'span';
  @Prop() textAlign?: TTextAlign = 'left';
  @Prop() weight?: TTextWeight; //@TODO: discuss to be removed
  @Prop() variant: TTextVariant = 'text';
  @Prop() color?: TTextColor = 'inherit';

  componentWillLoad() {
    injectFont();
  }

  render() {
    injectCSS(
      this.el,
      createStyles,
      this.tag,
      this.variant,
      this.color,
      this.textAlign,
      this.weight
    );

    return (
      <this.tag>
        <slot />
      </this.tag>
    );
  }
}
