import React, { useState, useEffect } from "react";
import { defineCustomElements } from '@boschrexroth/nextgen-web-ui-toolkit-react';
defineCustomElements();

const Preview2DApi = () => {
    const [data, setData] = useState(null);
    const [error, setError] = useState(null);
    const [selectedImage, setSelectedImage] = useState("");
    const [imageLoading, setImageLoading] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const params = {
                    apikey: 'e66120f9d9624823884cac1bf290ea88',
                    path: 'bosch_rexroth_cytro/power_units/std_power_units/cytropac_asmtab.prj'
                };
                const queryString = new URLSearchParams(params).toString();
                const url = `https://webapi.partcommunity.com/service/derivationviews?${queryString}`;
                const response = await fetch(url);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const result = await response.json();
                setData(result);
                if (result.views.length > 0) {
                    setSelectedImage(result.views[0].imageUrl);
                    setImageLoading(true);
                }
            } catch (error) {
                console.error('Error fetching the data', error);
                setError(error.message);
            }
        };

        fetchData();
    }, []);

    const handleSelectionChange = (event) => {
        setSelectedImage(event.target.value);
        setImageLoading(true);
    };

    const handleImageLoad = () => {
        setImageLoading(false);
    };

    if (error) {
        return <div>Error: {error}</div>;
    }

    if (!data) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <div style={{width: "300px",marginLeft: "20px",}}>
            <dc-ui-dropdown-wrapper label="Postitions" label-position="left">
            <select onChange={handleSelectionChange} value={selectedImage}>
                {data.views.map((item, index) => (
                    <option key={index} value={item.imageUrl}>
                        {item.desc}
                    </option>
                ))}
            </select>
            </dc-ui-dropdown-wrapper>
            </div>
            {selectedImage && (
                <div style={{width: '310px', height: '310px' ,border: "15px solid #cad6dd", padding: "10px"}} >
                    {imageLoading && <div>Fetching...</div>}
                    <img
                        src={`https://webapi.partcommunity.com${selectedImage}`}
                        onLoad={handleImageLoad}
                        style={{ width: '300px', height: '300px', display: imageLoading ? 'none' : 'block'}}
                        alt="Selected view"
                    />
                </div>
            )}
        </div>
    );
};

export default Preview2DApi;
