import React, { useState, useEffect } from 'react';
import Preview3DApi from './Preview3DApi';
import WebViewer3D from './WebViewer3D';
import Preview2DApi from './Preview2DApi';
import Modal from './Module';
import Footer from './Footer';
import boschrexrothlogo from './boschrexrothlogo.svg';
import { defineCustomElements } from '@boschrexroth/nextgen-web-ui-toolkit-react';
defineCustomElements();

const TableDataApi = ({ filepath }) => {
  const [data, setData] = useState(null);
  const [apiData, setApiData] = useState(null);
  const [miDent, setMiDent] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('english');
  const [selectedValues, setSelectedValues] = useState('');
  const [isEditing, setIsEditing] = useState(false); // State for toggling edit mode
  const [typecodeInfo, setTypecodeInfo] = useState(''); // State for TypecodeInfo

  const handleApiData = (data) => {
    setApiData(data);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const params = {
          apikey: 'e66120f9d9624823884cac1bf290ea88',
          language: selectedLanguage,
          eol: '1',
          plm: 'true',
          includeunclassifiedbycountry: 'false', 
          enablePreviewPerLine: 'true',
          path: filepath,
          includecrossref: 'true',
        };
        const queryString = new URLSearchParams(params).toString();
        const url = `https://webapi.partcommunity.com/service/table/vertical?${queryString}`;
        const response = await fetch(url);
        const result = await response.json();
        setData(result);
        if (result.index.NN === 'CytroBox') {
          setSelectedValues({
            PATH: result.index.path,
            LINEID: result.index.lineid,
            NB: result.index.NB,
            TYPE: result.index.line.values.TYPE.current,
            V: result.index.line.values.V.current,
            CF: result.index.line.values.CF.current,
            CC: result.index.line.values.CC.current,
            C: result.index.line.values.C.current,
            MPG1: result.index.line.values.MPG1.current,
            CD1: result.index.line.values.CD1.current,
            MPG2: result.index.line.values.MPG2.current,
            CD2: result.index.line.values.CD2.current,
            OC: result.index.line.values.OC.current,
            OT: result.index.line.values.OT.current,
            ST: result.index.line.values.ST.current
          });
        } else if (result.index.NN === 'CytroPac') {
          setSelectedValues({
            PATH: result.index.path,
            LINEID: result.index.lineid,
            NB: result.index.NB,
            TYPE: result.index.line.values.TYP.current,
            CS: result.index.line.values.CS.current,
            TS: result.index.line.values.TS.current,
            DR: result.index.line.values.DR.current,
            PC: result.index.line.values.PC.current,
            PM: result.index.line.values.PM.current,
            OP: result.index.line.values.OP.current,
            ST: result.index.line.values.ST.current,
            CT: result.index.line.values.CT.current,
            FL: result.index.line.values.FL.current,
            COL: result.index.line.values.COL.current,
            FD: result.index.line.values.FD.current
          });
        }
      } catch (error) {
        console.error('Error fetching the data', error);
      }
    };
    fetchData();
  }, [selectedLanguage, filepath]);

  useEffect(() => {
    updateMiDent();
  });

  const updateMiDent = () => {
    if (data && data.index && data.index.NN === 'CytroBox') {
      const newMiDent = `{${selectedValues.PATH}},013 {LINEID=${selectedValues.LINEID}} {NB=${selectedValues.NB}},
                        {TYPE=${selectedValues.TYPE}},{V=${selectedValues.V}},{CF=${selectedValues.CF}},{CC=${selectedValues.CC}},
                        {C=${selectedValues.C}},{MPG1=${selectedValues.MPG1}},{CD1=${selectedValues.CD1}},{MPG2=${selectedValues.MPG2}},
                        {CD2=${selectedValues.CD2}},{OC=${selectedValues.OC}},{OT=${selectedValues.OT}},{ST=${selectedValues.ST}},`;
      setMiDent(newMiDent);
    } else if (data && data.index && data.index.NN === 'CytroPac') {
      const newMiDent = `{${selectedValues.PATH}},013 {LINEID=${selectedValues.LINEID}} {NB=${selectedValues.NB}},
                        {TYP=${selectedValues.TYPE}},{CS=${selectedValues.CS}},{TS=${selectedValues.TS}},{DR=${selectedValues.DR}},
                        {PC=${selectedValues.PC}},{PM=${selectedValues.PM}},{OP=${selectedValues.OP}},{ST=${selectedValues.ST}},
                        {CT=${selectedValues.CT}},{FL=${selectedValues.FL}},{COL=${selectedValues.COL}},{FD=${selectedValues.FD}},
                        {PPATH=cytropac_asmtpl.prj},{BAH005=380},{AAF726=8.3},{AAB456=4000},{BAD915=AC},{AAC830=20},{BAC676=G1},
                        {AAN523=Plug-in connection},{AAG402=1},{AAQ326=https://www.boschrexroth.com/ics/cat/?cat=Industrial-Hydraulics-Catalog&p=p943945},
                        {AAJ413=G1},{BAI539=},{BAB706=}`;
      setMiDent(newMiDent);
    }
  };

  const handleChange = (field, value) => {
    setSelectedValues(prevValues => ({
      ...prevValues,
      [field]: value
    }));
  };

  const handleLanguageChange = (event) => {
    setSelectedLanguage(event.target.value);
  };

  const toggleEditMode = () => {
    setIsEditing(prev => !prev);
  };

  const handleTypecodeInfoChange = (event) => {
    setTypecodeInfo(event.target.value);
  };

  if (!data) {
    return <div>Loading...</div>;
  }

  const indexPath = data.index.line.values;

  const renderRow = (label, values, fieldName, units) => (
    <tr key={fieldName}>
      <td>{label}</td>
      <td>
        {values.length === 1 ? (
          values[0].name
        ) : (
          <dc-ui-dropdown-wrapper>
            <select onChange={(e) => handleChange(fieldName, e.target.value)}>
              {values.map((item, index) => (
                <option key={index} value={item.value}>{item.name}</option>
              ))}
            </select>
          </dc-ui-dropdown-wrapper>
        )}
      </td>
      <td>{units}</td>
    </tr>
  );

  let TypecodeInfoDisplay = '';
  let Tabledata = null;

  if (data && data.index && data.index.NN === 'CytroBox') {
    TypecodeInfoDisplay = `${selectedValues.TYPE.split(':')[0]}-${selectedValues.V}/${selectedValues.CF.split(':')[0]}${selectedValues.CC.split(':')[0]}${selectedValues.C.split(':')[0]}/${selectedValues.MPG1}${selectedValues.CD1}/${selectedValues.MPG2.split(':')[0]}${selectedValues.CD2.split(':')[0]}/${selectedValues.OC}${selectedValues.OT}/${selectedValues.ST}`;
    Tabledata = (
      <tbody>
        {renderRow(indexPath.MN.desc, indexPath.MN.values, 'MN', indexPath.MN.Unit)}
        {renderRow(indexPath.TYPE.desc, indexPath.TYPE.values, 'TYPE', indexPath.TYPE.Unit)}
        {renderRow(indexPath.V.desc, indexPath.V.values, 'V', indexPath.V.Unit)}
        {renderRow(indexPath.CF.desc, indexPath.CF.values, 'CF', indexPath.CF.Unit)}
        {renderRow(indexPath.CC.desc, indexPath.CC.values, 'CC', indexPath.CC.Unit)}
        {renderRow(indexPath.C.desc, indexPath.C.values, 'C', indexPath.C.Unit)}
        {renderRow(indexPath.MPG1.desc, indexPath.MPG1.values, 'MPG1', indexPath.MPG1.Unit)}
        {renderRow(indexPath.CD1.desc, indexPath.CD1.values, 'CD1', indexPath.CD1.Unit)}
        {renderRow(indexPath.MPG2.desc, indexPath.MPG2.values, 'MPG2', indexPath.MPG2.Unit)}
        {renderRow(indexPath.CD2.desc, indexPath.CD2.values, 'CD2', indexPath.CD2.Unit)}
        {renderRow(indexPath.OC.desc, indexPath.OC.values, 'OC', indexPath.OC.Unit)}
        {renderRow(indexPath.OT.desc, indexPath.OT.values, 'OT', indexPath.OT.Unit)}
        {renderRow(indexPath.ST.desc, indexPath.ST.values, 'ST', indexPath.ST.Unit)}
      </tbody>
    );
  } else if (data && data.index && data.index.NN === 'CytroPac') {
    TypecodeInfoDisplay = `${selectedValues.TYPE.split(':')[0]}-${selectedValues.CS.split(':')[0]}/${selectedValues.TS.split(':')[0]}/${selectedValues.DR}${selectedValues.PC}${selectedValues.PM}/${selectedValues.OP.split(':')[0]}/${selectedValues.ST}/${selectedValues.CT}/${selectedValues.FL.split(':')[0]}/${selectedValues.COL.split(':')[0]}`;
    Tabledata = (
      <tbody>
        {renderRow(indexPath.MN.desc, indexPath.MN.values, 'MN', indexPath.MN.Unit)}
        {renderRow(indexPath.TYP.desc, indexPath.TYP.values, 'TYP', indexPath.TYP.Unit)}
        {renderRow(indexPath.CS.desc, indexPath.CS.values, 'CS', indexPath.CS.Unit)}
        {renderRow(indexPath.TS.desc, indexPath.TS.values, 'TS', indexPath.TS.Unit)}
        {renderRow(indexPath.DR.desc, indexPath.DR.values, 'DR', indexPath.DR.Unit)}
        {renderRow(indexPath.PC.desc, indexPath.PC.values, 'PC', indexPath.PC.Unit)}
        {renderRow(indexPath.PM.desc, indexPath.PM.values, 'PM', indexPath.PM.Unit)}
        {renderRow(indexPath.OP.desc, indexPath.OP.values, 'OP', indexPath.OP.Unit)}
        {renderRow(indexPath.ST.desc, indexPath.ST.values, 'ST', indexPath.ST.Unit)}
        {renderRow(indexPath.CT.desc, indexPath.CT.values, 'CT', indexPath.CT.Unit)}
        {renderRow(indexPath.FL.desc, indexPath.FL.values, 'FL', indexPath.FL.Unit)}
        {renderRow(indexPath.COL.desc, indexPath.COL.values, 'COL', indexPath.COL.Unit)}
        {renderRow(indexPath.FD.desc, indexPath.FD.values, 'FD', indexPath.FD.Unit)}
      </tbody>
    );
  }

  const HeaderName = data.index.NT +' '+  data.index.NN;
  return (
    <div>
      <div>
        <div style={{ backgroundColor: "#ffffff" , paddingRight: "1%"}}>
          <div style={{ width: "15%", marginLeft: "auto", display: "block", backgroundColor: "#ffffff" }}>
            <dc-ui-dropdown-wrapper>
              <select value={selectedLanguage} onChange={handleLanguageChange}>
                <option value="english">English</option>
                <option value="german">German</option>
              </select>
            </dc-ui-dropdown-wrapper>
          </div>
        </div>
        <div style={{ backgroundColor: "#ffffff", display: "flex"}}>
          <dc-ui-image
            src={boschrexrothlogo} alt="" style={{ height: "5%", width: "10%", marginLeft: "2%", paddingTop: "0.5%" , paddingBottom: "0.5%"}}>
          </dc-ui-image>
          <dc-ui-button variant="link" size="small" type="submit" style={{ marginLeft: "75%", paddingTop: "0.8%" }}>
            <dc-ui-icon name="dc_customerservice" size="small" align="center"></dc-ui-icon><br></br>
            Contact
          </dc-ui-button>
          <dc-ui-button variant="link" size="small" type="submit" style={{ paddingTop: "0.8%" }}>
            <dc-ui-icon name="dc_user" size="small" align="center"></dc-ui-icon><br></br>
            myrexroth
          </dc-ui-button>
          <dc-ui-button variant="link" size="small" type="submit" style={{ paddingTop: "0.8%" }}>
            <dc-ui-icon name="dc_cart" size="small" align="center"></dc-ui-icon><br></br>
            shopping cart
          </dc-ui-button>
        </div>
        <div style={{ background: "linear-gradient(to bottom, #cad6dd, white)", display: "flex", alignItems: "center", height: "10vh", padding: "0 1%" }}>
          <dc-ui-text variant="h4" color="inherit" weight="bold" text-align="left"
            style={{ marginLeft: "1%", whiteSpace: "nowrap", overflow: "hidden", textOverflow: "ellipsis", flex: "1" }}>
            {HeaderName}
          </dc-ui-text>
          <dc-ui-button variant="secondary" size="small" type="submit" style={{ marginLeft: "auto" }}>
            Consultation request
          </dc-ui-button>
        </div>
        <div style={{paddingLeft: "1%", paddingRight: "1%"}}>
          <div style={{ display: 'flex', alignItems: 'center', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.4)', backgroundColor: '#FFFFFF'}}>
            <div style={{ backgroundColor: '#a3bac8', padding: '0.5%', textAlign: 'center', marginRight: '2%'}}>
              <dc-ui-text tag="span" variant="h5" color= "darkBlue02" weight="normal" text-align="center">
                Type code
              </dc-ui-text>
            </div>
            {isEditing ? (
              <input
                type="text"
                value={typecodeInfo}
                onChange={handleTypecodeInfoChange}
                style={{ flex: 1, marginRight: '2%' }}
              />
            ) : (
              <dc-ui-text tag="span" variant="h5" color="inherit" weight="bold" text-align="center" style={{ marginRight: '2%' }}>
                {TypecodeInfoDisplay}
              </dc-ui-text>
            )}
            <button onClick={toggleEditMode} style={{ marginLeft: '10px' }}>
              {isEditing ? 'Save' : 'Edit'}
            </button>
          </div>
        </div>
        <div style={{ display: 'flex', alignItems: 'flex-start',paddingLeft: "1%", paddingRight: "1%" }}>
          <div style={{ flex: 1 }}>
            <center>
              <h3>Product 3D View</h3>
            </center>
            <Preview3DApi miDent={miDent} onDataFetch={handleApiData} />
            <WebViewer3D apiData={apiData} />
            <div style={{ marginLeft: '30%', paddingTop: '5%' }}>
              <Modal />
            </div>
          </div>
          <div style={{ flex: 1, marginLeft: '1.7%' }}>
            <center>
              <h3>Product 2D View</h3>
            </center>
            <Preview2DApi filepath = {filepath}/>
          </div>

          <div style={{ flex: 1}}>
            <center>
              <h3>Product Details</h3>
            </center>
            <div style={{ marginRight: "0vh", marginLeft: "auto", width: "80vh", overflow: "hidden", border: "2px solid rgba(0, 0, 0, 0.6)", padding: "1vh" }}>
              <dc-ui-table>
                <table>
                  {Tabledata}
                </table>
              </dc-ui-table>
            </div>
          </div>
        </div>
        </div>
        <Footer />
    </div>
  );
};

export default TableDataApi;
