  @Prop() variant: TButtonVariant = 'secondary';
  /** Adds an icon to the button. Default value is an empty string. */
  @Prop() icon?: TIcon;
  /** Adds custom icon to the button. Using URL */
  @Prop() iconSource?: string;
  /** Hides the label on the button. Default value is false. */
  @Prop() hideLabel = false;
  /** Disables the button on the interface. Default value is false. */
  @Prop() disabled = false;
  /** Adds an alignment to the button. Default value is 'center'. */
  @Prop() align?: TButtonAlign;
  /** (optional) Adds a name to the button. This is useful for form submissions. */
  @Prop() name?: string;
  @Prop() aria?: SelectedAriaAttributes<TButtonAriaAttributes>;

  componentWillLoad() {
