    &:active {
      &:not(.disabled):not([disabled]) {
        color: var(--dc-ui-link-primary-active);
      }
    }

    &:hover {
      &:not(.disabled):not([disabled]) {
        color: var(--dc-ui-link-primary-hover);
      }
    }

    &.disabled,
    &[disabled] {
      cursor: not-allowed;
      color: var(--dc-ui-link-primary-disabled);
    }
  }

  &--secondary {
    color: var(--dc-ui-link-secondary-color);

    &:active {
      &:not(.disabled):not([disabled]) {
        color: var(--dc-ui-link-secondary-active);
      }
    }

    &:hover {
      &:not(.disabled):not([disabled]) {
        color: var(--dc-ui-link-secondary-hover);
      }
    }

    &.disabled,
    &[disabled] {
      cursor: not-allowed;
      color: var(--dc-ui-link-secondary-disabled);
    }
  }

  &--small {
    dc-ui-icon {
      margin-right: 0.625rem;
    }
  }

  &--normal {
    dc-ui-icon {
      margin-right: 8px;
    }
  }

  &--underlined {
    dc-ui-text {
      text-decoration: underline;
    }
  }
