    }
  }

  componentDidRender() {
    this.modal = this.el.shadowRoot.querySelector('dialog');

    if (this.isOpen) {
      setScrollLock(true);
      this.modal.showModal();
    }

    this.observeAttributes(); // once initially

    if (this.triggerEl) {
      addTriggerListeners(this);
    }
  }

  disconnectedCallback() {
    setScrollLock(false);
  }

  // method to open the modal
  @Method() open(): Promise<boolean> {
    return new Promise((resolve) => {
      this.modal.showModal();
      resolve(true);
    });
  }

  // method to close the modal
  @Method() close(): Promise<boolean> {
    return new Promise((resolve) => {
      this.modal.close();
      resolve(true);
    });
  }

  onTriggerClick = () => {
    this.modal.showModal();
  };

  private observeAttributes = (): void => {
    observeAttributes(this.modal, ['open'], () => (this.isOpen = this.modal.hasAttribute('open')));
  };

  // close modal on backdrop click if noBackdropClick is false
  private onBackdropClick = (e: MouseEvent) => {
    if (!this.noBackdropClick && e.target === this.modal) {
      this.close();
    }
  };

  returnCSSClasses = () => {
    return {
      'dc-ui-modal': true,
      [`dc-ui-modal--${this.maxWidth}`]:
        this.maxWidthFlexible === undefined ||
        this.maxWidthFlexible === null ||
        Number.isNaN(this.maxWidthFlexible),
      'dc-ui-modal--sticky-header': this.stickyHeader,
      'dc-ui-modal--sticky-footer': this.stickyFooter,
      'dc-ui-modal--mobile-fullscreen': this.mobileFullscreen,
      'dc-ui-modal--backdrop-dark': this.backdropColor === 'dark',
    };
  };

  render() {
    // only add max-width using maxWidthFlexible if maxWidthFlexible is set. If not do not add it
    const styles = {
      maxWidth: this.maxWidthFlexible ? this.maxWidthFlexible + 'px' : '',
    };

    return (
      <dialog
        onClick={this.onBackdropClick}
        class={this.returnCSSClasses()}
        style={{ ...styles }}>
        <div class="dialog-body">
          <div class="dialog-content">
            <div class="dialog-header">
              {this.modalTitle && !this.hasHeaderSlot && (
                <dc-ui-headline
                  no-margin="true"
                  tag="h2"
                  variant="h4"
                  color="darkBlue02">
                  {this.modalTitle}
                </dc-ui-headline>
              )}
              {this.closeIcon && (
                <dc-ui-button
                  id="close-dialog"
                  icon="dc_close"
                  onClick={() => this.close()}
                  variant="link"
                  size="small"
                  hide-label="true">
                  Close modal
                </dc-ui-button>
              )}
              <slot name="header" />
            </div>
            <div class="dialog-main">
              <slot />
            </div>
            {this.hasFooterSlot && (
              <div class="dialog-footer">
                <slot name="footer" />
              </div>
            )}
          </div>
        </div>
      </dialog>
    );
  }
}
import { getCss, buildResponsiveStyles } from '../../utils';
import { TSpacerDirection, SpacerDirectionType, TSpacerSize, SpacerSizeType } from './spacer-utils';

export const createStyles = (direction: TSpacerDirection, size: TSpacerSize): string => {
  return getCss({
    '@global': {
      ':host': buildResponsiveStyles(direction, (directionResponsive: SpacerDirectionType) => ({
        display: directionResponsive === 'horizontal' ? 'block' : 'inline-block',
        ...buildResponsiveStyles(size, (sizeResponsive: SpacerSizeType) => ({
          height: directionResponsive === 'horizontal' ? `${sizeResponsive}px` : 'auto',
          width: directionResponsive === 'horizontal' ? '100%' : `${sizeResponsive}px`,
        })),
      })),
    },
  });
};
import { Component, h, Prop, Element, Host } from '@stencil/core';
import { injectCSS } from '../../utils';
import { createStyles } from './spacer-styles';
import { TSpacerDirection, TSpacerSize } from './spacer-utils';

@Component({
  tag: 'dc-ui-spacer',
  shadow: true,
})
export class DcUiSpacer {
  @Element() el: HTMLElement;
  @Prop() size: TSpacerSize = 8;
  @Prop() direction: TSpacerDirection = 'horizontal';

  render() {
    injectCSS(this.el, createStyles, this.direction, this.size);

    return <Host></Host>;
  }
}
import {
  getCss,
  buildResponsiveStyles,
  buildResponsiveTextStyles,
  mergeDeep,
  color as colorUtils,
} from '../../utils';
import {
  TTextTag,
  TextAlignType,
  TTextAlign,
  TTextVariant,
  TextColorType,
  TTextColor,
  TTextWeight,
} from './text-utils';

export const createStyles = (
  tag: TTextTag,
  variant: TTextVariant,
  color: TTextColor,
  align: TTextAlign,
  weight: TTextWeight
): string => {
  const styles = getCss({
    '@global': {
      ':host': {
        // if default tag is displayed as block use display: block
        display: 'block',
      },
      [tag as string]: {
        display: 'inline-block',
        fontFamily: 'Bosch Sans',
        textDecoration: 'inherit',
        margin: '0',
        ...mergeDeep(
          buildResponsiveTextStyles(variant as string),
          buildResponsiveStyles(color, (colorResponsive: TextColorType) => ({
            color: colorResponsive === 'inherit' ? 'inherit' : colorUtils[colorResponsive],
          })),
          buildResponsiveStyles(align, (alignResponsive: TextAlignType) => ({
            textAlign: alignResponsive,
          })),
          weight
            ? buildResponsiveStyles(weight, (weightResponsive: TTextWeight) => ({
                fontWeight: weightResponsive,
              }))
            : {}
        ),
      },
    },
  });

  return styles;
};
import { Component, h, Prop, Element } from '@stencil/core';
import { injectFont, injectCSS } from '../../utils';
import { TTextTag, TTextAlign, TTextWeight, TTextVariant, TTextColor } from './text-utils';
import { createStyles } from './text-styles';

@Component({
  tag: 'dc-ui-text',
  shadow: true,
})
export class DcUiText {
  @Element() el: HTMLElement;
  @Prop() tag: TTextTag = 'span';
  @Prop() textAlign?: TTextAlign = 'left';
  @Prop() weight?: TTextWeight; //@TODO: discuss to be removed
  @Prop() variant: TTextVariant = 'text';
  @Prop() color?: TTextColor = 'inherit';

  componentWillLoad() {
    injectFont();
  }

  render() {
    injectCSS(
      this.el,
      createStyles,
      this.tag,
      this.variant,
      this.color,
      this.textAlign,
      this.weight
    );

    return (
      <this.tag>
        <slot />
      </this.tag>
    );
  }
}
