import { getCss, color as colorUtils } from '../../utils';

export const createStyles = (open: boolean): string => {
  return getCss({
    '@global': {
      ':host': {
        display: 'block',
        boxSizing: 'border-box',
      },
      '.accordion__heading': {
        color: colorUtils.darkBlue02,
        padding: 0,
        margin: 0,
        backgroundColor: colorUtils.white,

        '&--disabled': {
          color: colorUtils.grey03,
        },

        '&:not(.accordion__heading--disabled):not(.accordion__heading--list):hover': {
          backgroundColor: colorUtils.grey01,
        },
      },
      '.accordion__button': {
        appearance: 'none',
        WebkitAppearance: 'none',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        width: '100%',
        padding: '20px 16px',
        border: 'none',
        borderTop: `1px solid ${colorUtils.grey03}`,
        background: 'none',
        cursor: 'pointer',
        color: 'inherit',
        outline: '2px solid transparent',
        outlineOffset: '-2px',

        '&--last': {
          borderBottom: `1px solid ${colorUtils.grey03}`,
        },

        '&--list': {
          padding: '8px',
          borderTop: 'none',
        },

        '&:disabled': {
          cursor: 'not-allowed',
        },

        '&:focus': {
          outlineColor: colorUtils.turquoise03,
        },
      },
      'dc-ui-icon': {
        transition: 'rotate 0.3s ease-in-out',
        rotate: open ? 'x 180deg' : '0deg',
      },
      '.accordion__icon': {
        '&--hidden': {
          display: 'none',
        },
      },
      '.accordion__content': {
        display: 'none',
        padding: '8px 16px 64px',
        backgroundColor: colorUtils.white,

        '&--list': {
          borderTop: `1px solid ${colorUtils.grey03}`,
          padding: '15px 8px',
        },

        '&--open': {
          display: 'block',
        },
      },
    },
  });
};
import { Component, Element, Event, EventEmitter, h, Host, Method, Prop } from '@stencil/core';
import { injectCSS } from '../../utils';
import { createStyles } from './accordion-styles';
import { TAccordionChangeEvent, TAccordionHeading, TAccordionVariant } from './accordion-utils';

@Component({
  tag: 'dc-ui-accordion',
  shadow: true,
})
export class DcUiAccordion {
  @Element() el: HTMLElement;
  @Prop() title: string; // TODO: use different prop name, hence title is already used by HTML
  @Prop() heading: TAccordionHeading = 'h2';
  @Prop({ reflect: true }) variant: TAccordionVariant = 'default';
  @Prop() disabled = false;
  @Prop() public open: boolean;
  /** Emitted when accordion state is changed. */
  @Event({ bubbles: false }) public accordionChange: EventEmitter<TAccordionChangeEvent>;

  @Method() async toggle(): Promise<boolean> {
    this.accordionChange.emit({ open: !this.open });
    this.open = !this.open;
    return this.open;
  }

  private isLastChild = false;

  componentWillLoad() {
    // check if next sibling is dc-ui-accordion
    this.isLastChild = this.el.nextElementSibling?.tagName !== 'DC-UI-ACCORDION';
  }

  private onButtonClick = (): void => {
    this.accordionChange.emit({ open: !this.open });
    this.open = !this.open;
  };

  render() {
    injectCSS(this.el, createStyles, this.open);

    const Heading = this.heading;
    const buttonId = 'accordion-control';
    const contentId = 'accordion-panel';

    const iconVariantDefault = (
      <dc-ui-icon
        aria-hidden="true"
        name="dc_down"></dc-ui-icon>
    );

    const iconVariantList = (
      <div>
        <dc-ui-icon
          aria-hidden="true"
          size="tiny"
          class={{
            'accordion__icon--hidden': this.open,
          }}
          name="dc_add"></dc-ui-icon>
        <dc-ui-icon
          aria-hidden="true"
          size="tiny"
          class={{
            'accordion__icon--hidden': !this.open,
          }}
          name="dc_less_minimize"></dc-ui-icon>
      </div>
    );

    return (
      <Host>
        <Heading
          class={{
            accordion__heading: true,
            'accordion__heading--disabled': this.disabled,
            'accordion__heading--list': this.variant === 'list',
          }}>
          <button
            class={{
              accordion__button: true,
              'accordion__button--list': this.variant === 'list',
              'accordion__button--last': this.isLastChild && this.variant !== 'list',
            }}
            id={buttonId}
            type="button"
            {...(this.disabled && { disabled: true })}
            aria-expanded={this.open ? 'true' : 'false'}
            aria-controls={contentId}
            onClick={this.onButtonClick}>
            <dc-ui-text
              color="inherit"
              variant={this.variant === 'default' ? 'h4' : 'mid-size-text'}>
              {this.title}
            </dc-ui-text>
            {this.variant === 'default' ? iconVariantDefault : iconVariantList}
          </button>
        </Heading>
        <div
          id={contentId}
          role="region"
          aria-labelledby={buttonId}
          class={{
            accordion__content: true,
            'accordion__content--open': this.open,
            'accordion__content--list': this.variant === 'list',
          }}>
          <slot />
        </div>
      </Host>
    );
  }
}
