export type TLinkButtonSize = 'small' | 'normal' | 'large';
export const LINKBUTTON_VARIANTS = ['primary', 'secondary', 'tertiary', 'light', 'link'] as const;
export type TLinkButtonVariant = typeof LINKBUTTON_VARIANTS[number];
export type TLinkButtonAlign = 'left' | 'center' | 'right';
export const LINK_ARIA_ATTRIBUTES = ['aria-label'] as const;
export type TLinkAriaAttribute = typeof LINK_ARIA_ATTRIBUTES[number];
// button styles
$dc-ui-link-button-animation-duration: 0.35s;
$dc-ui-link-button-focus-color: $color-turquoise-03;
$dc-ui-link-button-focus-color-inset: $color-white;

// primary button
$dc-ui-link-button-primary-bg: $color-red-brand-01;
$dc-ui-link-button-primary-bg-hover: $color-red-brand-02;
$dc-ui-link-button-primary-bg-active: $color-red-brand-03;
$dc-ui-link-button-primary-color: $color-white;
$dc-ui-link-button-primary-disabled-bg: $color-grey-02;
$dc-ui-link-button-primary-disabled-color: $color-grey-03;

// secondary button
$dc-ui-link-button-secondary-bg: $color-turquoise-01;
$dc-ui-link-button-secondary-bg-hover: $color-turquoise-02;
$dc-ui-link-button-secondary-bg-active: $color-turquoise-03;
$dc-ui-link-button-secondary-color: $color-turquoise-06;
$dc-ui-link-button-secondary-disabled-bg: $color-grey-02;
$dc-ui-link-button-secondary-disabled-color: $color-grey-03;

// tertiary button
$dc-ui-link-button-tertiary-bg: $color-grey-06;
$dc-ui-link-button-tertiary-bg-hover: $color-grey-07;
$dc-ui-link-button-tertiary-bg-active: $color-grey-08;
$dc-ui-link-button-tertiary-color: $color-white;
$dc-ui-link-button-tertiary-color-hover: $color-grey-10;
$dc-ui-link-button-tertiary-disabled-bg: $color-grey-02;
$dc-ui-link-button-tertiary-disabled-color: $color-grey-03;

// light button
$dc-ui-link-button-light-bg: $color-grey-03;
$dc-ui-link-button-light-bg-hover: $color-grey-04;
$dc-ui-link-button-light-bg-active: $color-grey-05;
$dc-ui-link-button-light-color: $color-dark-blue-02;
$dc-ui-link-button-light-color-hover: $color-grey-10;
$dc-ui-link-button-light-disabled-bg: $color-grey-02;
$dc-ui-link-button-light-disabled-color: $color-grey-03;

// link button
$dc-ui-link-button-link-bg: transparent;
$dc-ui-link-button-link-bg-hover: $color-grey-01;
$dc-ui-link-button-link-bg-active: $color-grey-02;
$dc-ui-link-button-link-color: $color-grey-06;
$dc-ui-link-button-link-color-hover: $color-grey-10;
$dc-ui-link-button-link-disabled-bg: transparent;
$dc-ui-link-button-link-disabled-color: $color-grey-03;

@media screen and (prefers-reduced-motion: reduce) {
  $dc-ui-link-button-animation-duration: 0s;
}

:host {
  display: block;
  width: auto;
}

:host(.inline) {
  display: inline-block;
}

.dc-ui-link-button {
  align-content: center;
  align-items: center;
  border: none;
  cursor: inherit;
  display: inline-flex;
  gap: 7px;
  justify-content: center;
  outline: 0;
  padding: 0;
  position: relative;
  text-transform: uppercase;
  user-select: none;
  vertical-align: middle;
  white-space: nowrap;
  width: auto;
  text-decoration: none;
  cursor: pointer;

  background-size: 300% 300%;
  background-position: 0 0;
  animation: animation-out 350ms forwards;

  & > span {
    font-family: 'Bosch sans';
  }

  &.disabled,
  &[disabled] {
    cursor: not-allowed;
  }

  &:focus {
    &:before {
      content: '';
      position: absolute;
      inset: 0;
      border: 2px solid $dc-ui-link-button-focus-color;
      z-index: 3;
    }

    &:after {
      content: '';
      position: absolute;
      inset: 2px;
      border: 1px solid $dc-ui-link-button-focus-color-inset;
      z-index: 3;
    }
  }

  &--align-left {
    justify-content: flex-start;
  }

  &--align-right {
    justify-content: flex-end;
  }

  &--primary {
    background-color: $dc-ui-link-button-primary-bg;
    color: $dc-ui-link-button-primary-color;

    background-image: linear-gradient(
      -4deg,
      transparent 33%,
      $dc-ui-link-button-primary-bg-hover 34%,
      $dc-ui-link-button-primary-bg-hover 66%,
      transparent 67%
    );

    &:active:not(.disabled):not([disabled]) {
      background-image: none;
      background-color: $dc-ui-link-button-primary-bg-active;
    }

    &.disabled,
    &[disabled] {
      background-color: $dc-ui-link-button-primary-disabled-bg;
      color: $dc-ui-link-button-primary-disabled-color;
    }
  }

  &--secondary {
    background-color: $dc-ui-link-button-secondary-bg;
    color: $dc-ui-link-button-secondary-color;

    background-image: linear-gradient(
      -4deg,
      transparent 33%,
      $dc-ui-link-button-secondary-bg-hover 34%,
      $dc-ui-link-button-secondary-bg-hover 66%,
      transparent 67%
    );

    &:active:not(.disabled):not([disabled]) {
      background-image: none;
      background-color: $dc-ui-link-button-secondary-bg-active;
    }

    &.disabled,
    &[disabled] {
      background-color: $dc-ui-link-button-secondary-disabled-bg;
      color: $dc-ui-link-button-secondary-disabled-color;
    }
  }

  &--tertiary {
    background-color: $dc-ui-link-button-tertiary-bg;
    color: $dc-ui-link-button-tertiary-color;

    background-image: linear-gradient(
      -4deg,
      transparent 33%,
      $dc-ui-link-button-tertiary-bg-hover 34%,
      $dc-ui-link-button-tertiary-bg-hover 66%,
      transparent 67%
    );

    &:active:not(.disabled):not([disabled]) {
      background-image: none;
      background-color: $dc-ui-link-button-tertiary-bg-active;
    }

    &.disabled,
    &[disabled] {
      background-color: $dc-ui-link-button-tertiary-disabled-bg;
      color: $dc-ui-link-button-tertiary-disabled-color;
    }
  }

  &--light {
    background-color: $dc-ui-link-button-light-bg;
    color: $dc-ui-link-button-light-color;

    &:hover,
    &:focus,
    &:active {
      color: $dc-ui-link-button-light-color-hover;
    }

    background-image: linear-gradient(
      -4deg,
      transparent 33%,
      $dc-ui-link-button-light-bg-hover 34%,
      $dc-ui-link-button-light-bg-hover 66%,
      transparent 67%
    );

    &:active:not(.disabled):not([disabled]) {
      background-image: none;
      background-color: $dc-ui-link-button-light-bg-active;
    }

    &.disabled,
    &[disabled] {
      background-color: $dc-ui-link-button-light-disabled-bg;
      color: $dc-ui-link-button-light-disabled-color;
    }
  }

  &--link {
    background-color: $dc-ui-link-button-link-bg;
    color: $dc-ui-link-button-link-color;

    &:hover,
    &:focus,
    &:active {
      color: $dc-ui-link-button-link-color-hover;
    }

    background-image: linear-gradient(
      -4deg,
      transparent 33%,
      $dc-ui-link-button-link-bg-hover 34%,
      $dc-ui-link-button-link-bg-hover 66%,
      transparent 67%
    );

    &:active:not(.disabled):not([disabled]) {
      background-image: none;
      background-color: $dc-ui-link-button-link-bg-active;
    }

    &.disabled,
    &[disabled] {
      background-color: $dc-ui-link-button-link-disabled-bg;
      color: $dc-ui-link-button-link-disabled-color;
    }
  }

  &--small {
    height: 32px;
    padding: 0 15px;
    font-size: 0.75rem;
    line-height: 1rem;
    font-weight: 900;

    &.dc-ui-link-button--icon:not(.dc-ui-link-button--hide-label) {
      padding: 0 15px 0 16px;
    }

    &.dc-ui-link-button--hide-label {
      padding: 0 6px;
    }
  }

  &--normal {
    height: 48px;
    padding: 0 15px;
    font-size: 1rem;
    line-height: 1.25rem;
    font-weight: 900;

    &.dc-ui-link-button--icon:not(.dc-ui-link-button--hide-label) {
      padding: 0 15px 0 16px;
    }

    &.dc-ui-link-button--hide-label {
      padding: 0 12px;
    }
  }

  &--large {
    height: 54px;
    padding: 0 15px;
    font-size: 1.25rem;
    line-height: 1.375rem;
    font-weight: 900;

    &.dc-ui-link-button--icon:not(.dc-ui-link-button--hide-label) {
      padding: 0 15px 0 16px;
    }

    &.dc-ui-link-button--hide-label {
      padding: 0 13px;
    }
  }

  &--hide-label {
    gap: 0;

    & > span {
      display: block;
      width: 1px;
      text-size-adjust: none;
      height: 1px;
      margin: 0px 0px 0px -1px;
      overflow: hidden;
    }
  }

  &:hover:not(.disabled):not([disabled]) {
    animation: animation-in 350ms forwards;
  }
}

@keyframes animation-out {
  from {
    z-index: inherit;
    background-position: 50% 50%;
  }
  to {
    background-position: 100% 100%;
  }
}

@keyframes animation-in {
  from {
    z-index: inherit;
    background-position: 0 0;
  }
  to {
    z-index: inherit;
    background-position: 50% 50%;
  }
}
import { Component, Element, Host, Prop, h, Watch } from '@stencil/core';
import { SelectedAriaAttributes, parseAndGetAriaAttributes } from '../../utils';
import { TIcon } from '../../svg/icons';
import {
  LINKBUTTON_VARIANTS,
  TLinkAriaAttribute,
  TLinkButtonAlign,
  TLinkButtonSize,
  TLinkButtonVariant,
} from './link-button-utils';

@Component({
  tag: 'dc-ui-link-button',
  styleUrl: 'dc-ui-link-button.scss',
  shadow: true,
})
export class DcUiLinkButton {
  @Element() host: HTMLElement;
  link: HTMLAnchorElement;

  /** Adds a size to the link button. Default value is 'normal'.*/
  @Prop() size?: TLinkButtonSize = 'normal';
  /** Adds a variant to the link button. Default value is 'secondary'. */
  @Prop() variant?: TLinkButtonVariant = 'secondary';
  /** Adds the href attr to the link button. No default value is set. */
  @Prop() href: string;
  /** Adds the target attr to the link button. Default value is _self. */
  @Prop() target?: string = '_self';
  /** Adds an icon to the link button. No default value is an empty string. */
  @Prop() icon?: TIcon;
  /** Adds alternatively an icon via string to the link button. Default value is an empty string. */
  @Prop() iconSource?: string;
  /** Adds prop download to the link button. Default value is false. */
  @Prop() download?: boolean = false;
  /** Hides the label on the button. Default value is false. */
  @Prop() hideLabel = false;
  /** Adds prop disabled to the link button. Default value is false. */
  @Prop({ reflect: true }) disabled?: boolean = false;
  /** Set the host and his child to inline or block element. Default value is true. */
  @Prop() inline?: boolean = true;
  /** Adds an alignment to the link button. Default value is 'center'. */
  @Prop() align?: TLinkButtonAlign = 'left';
  /** Adds aria prop object to the link button. Default value is an empty string. */
  @Prop() aria?: SelectedAriaAttributes<TLinkAriaAttribute>;

  isComponentValid = true;

  @Watch('variant')
  validateVariant(variant: TLinkButtonVariant) {
    if (!LINKBUTTON_VARIANTS.includes(variant)) {
      this.isComponentValid = false;
      throw new Error(`Invalid variant: ${variant}`);
    }
  }

  @Watch('href')
  checkForHref() {
    if (!this.href || this.href === '') {
      this.disabled = true;
    }
  }

  componentWillLoad() {
    this.checkForHref();
    this.validateVariant(this.variant);
  }

  render() {
    const hrefAttribute = !this.disabled ? { href: this.href } : {};
    const downloadAttribute = this.download ? { download: this.download } : {};
    const disabledAttribute = this.disabled ? { ariaDisabled: this.disabled, role: 'link' } : {};

    if (this.isComponentValid) {
      return (
        <Host
          class={`
            ${this.inline ? 'inline' : ''}
          `}>
          <a
            class={`
              dc-ui-link-button
              dc-ui-link-button--${this.size}
              dc-ui-link-button--${this.variant}
              ${this.hideLabel && this.icon ? 'dc-ui-link-button--hide-label' : ''}
              ${this.disabled ? 'disabled' : ''}
              ${this.align ? `dc-ui-link-button--${this.align}` : ''}
              ${this.icon ? 'dc-ui-link-button--icon' : ''}
            `}
            {...hrefAttribute}
            {...downloadAttribute}
            {...disabledAttribute}
            {...parseAndGetAriaAttributes(this.aria)}
            target={this.target}>
            {this.icon || this.iconSource ? (
              <dc-ui-icon
                aria-hidden="true"
                name={this.icon}
                source={this.iconSource}
                size={this.size === 'normal' ? 'medium' : this.size}></dc-ui-icon>
            ) : null}

            <span>
              <slot />
            </span>
          </a>
        </Host>
      );
    }
  }
}
